INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-han-river-festival', 'Сеул 2020-2029 — Фестиваль реки Хан', 'Игрок посещает Hangang Park, катается на велосипеде, устраивает пикник с chimaek и наблюдает вечерний фейерверк.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-han-river-festival", "title": "Сеул 2020-2029 — Фестиваль реки Хан", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:35:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["seoul", "han_river", "festival"], "topics": ["urban-relaxation", "outdoor-activities"], "related_systems": ["narrative-service", "tourism-system", "buff-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/seoul/2020-2029/quest-007-han-river-festival.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Фестиваль реки Хан был в Markdown и не активировал механики отдыха и снижения стресса.", "goal": "Перевести события на реке Хан в YAML, подчеркнуть велопрогулку, пикник и фейерверки.", "essence": "Игрок посещает Hangang Park, катается на велосипеде, устраивает пикник с chimaek и наблюдает вечерний фейерверк.", "key_points": ["Квест-id SEOUL-2029-007, сложность easy, длительность 1–2 часа.", "Этапы включают аренду велосипеда, пикник и вечернее представление.", "Награды снимают 50% стресса, выдают 600 XP и титул «Пикник на Хан»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Han River Festival — главный городской праздник Сеула; парки вдоль реки превращаются в зону отдыха с велосипедами, пикниками и шоу фонтанов.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Прибыть в Yeouido Hangang Park и арендовать велосипед.\\n2. Прокатиться по велодорожке вдоль реки, изучая мосты и фонтанные шоу.\\n3. Организовать пикник с chimaek, наблюдая за жизнью сеульцев.\\n4. Завершить вечер фейерверком и ночными огнями мостов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны 12 парков Hangang, мосты, фонтанные шоу, популярность доставки жареной курицы и пива прямо в парк.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 30 едди, получает 600 XP, снижение стресса на 50%, социальный бафф «Пикник на Хан» и титул «Пикник на Хан».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890426, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Фестиваль реки Хан» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.re