INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-taekwondo-dojo', 'Сеул 2020-2029 — Додзё тхэквондо', 'Игрок записывается в додзё, проходит тренировки, сдаёт экзамены по поясам и становится мастером тхэквондо.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-taekwondo-dojo", "title": "Сеул 2020-2029 — Додзё тхэквондо", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:35:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["seoul", "taekwondo", "martial_arts"], "topics": ["combat-training", "cultural-sport"], "related_systems": ["narrative-service", "combat-system", "progression-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/seoul/2020-2029/quest-008-taekwondo-dojo.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Обучение тхэквондо находилось в Markdown и не отражало прогрессию по поясам и боевые баффы.", "goal": "Структурировать тренировочный процесс в YAML, описать poomsae, спарринг и экзамен на чёрный пояс.", "essence": "Игрок записывается в додзё, проходит тренировки, сдаёт экзамены по поясам и становится мастером тхэквондо.", "key_points": ["Квест-id SEOUL-2029-008, сложность medium, длительность 2–4 часа.", "Этапы охватывают poomsae, sparring, kyekpa и прогрессию по 6 поясам.", "Награда выдаёт 5 000 XP, +20 к рукопашному бою и титул «Мастер тхэквондо»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Taekwondo — олимпийское боевое искусство Кореи, акцентирующее высокие удары ногами, дисциплину и уважение.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Записаться в додзё и освоить базовые команды («Charyeot», «Kyeongne»).\\n2. Изучить poomsae, отрабатывая связки под счёт тренера.\\n3. Провести тренировочные спарринги (kyorugi) для повышения реакции.\\n4. Пройти kyekpa, разбив доски ударами ног.\\n5. Сдать экзамены на пояса от белого до чёрного и получить сертификат мастера.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны традиции dobok, значение поясов, команды на корейском и роль taekwondo в культуре и армии Кореи.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 5 000 XP, +20 к рукопашному бою, титул «Мастер тхэквондо» и доступ к боевым контрактам, связанным с федерацией.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890426, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Додзё тхэквондо» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

