INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-boston-bioengineering-breakthrough', 'Boston 2020-2029 — «Прорыв в био-инженерии»', 'Бостон как центр медицинских инноваций, где передовые технологии лечения сталкиваются с фундаментальными этическими вопросами.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-boston-bioengineering-breakthrough", "title": "Boston 2020-2029 — «Прорыв в био-инженерии»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:51:56+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["boston", "bioengineering", "medical", "breakthrough", "ethics"], "topics": ["medical", "ethics", "innovation"], "related_systems": ["narrative-service", "medical-system", "reputation-system"], "related_documents": [{"id": "canon-region-america-boston-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-013-bioengineering-breakthrough.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гарвардская медицинская школа разрабатывает революционный метод лечения нейродегенеративных заболеваний, но этические вопросы вызывают протесты.", "goal": "Игрок помогает завершить клинические испытания, балансируя между научным прогрессом и этическими дилеммами.", "essence": "Бостон как центр медицинских инноваций, где передовые технологии лечения сталкиваются с фундаментальными этическими вопросами.", "key_points": ["Наномедицина для лечения болезни Альцгеймера и Паркинсона.", "Протесты биоэтиков и религиозных групп.", "Финальное решение о доступности лечения для всех."]}, "content": {"sections": [{"id": "medical_research", "title": "Медицинские исследования", "body": "Игрок встречает ведущего исследователя в лаборатории Гарварда. Ему объясняют концепцию нано-ботов для восстановления нейронных связей.\\n", "mechanics_links": [], "assets": []}, {"id": "ethical_debate", "title": "Этическая дискуссия", "body": "Биоэтики организуют протесты против \\"неестественного вмешательства\\". Игрок участвует в дебатах и собирает мнения разных сторон.\\n", "mechanics_links": [], "assets": []}, {"id": "clinical_trials", "title": "Клинические испытания", "body": "Мини-игра: наблюдение за ходом испытаний и мониторинг побочных эффектов. Игрок должен предотвратить осложнения.\\n", "mechanics_links": [], "assets": []}, {"id": "breakthrough_announcement", "title": "Объявление прорыва", "body": "Финальная пресс-конференция с результатами испытаний. Игрок защищает проект от радикальных активистов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1200 XP.\\n- Издержки: −55 кредов.\\n- Бафф «Медицинский пионер +30% сопротивления болезням на 18 часов».\\n- Ачивка «Этический целитель».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Nano-Medicine", "definition": "Медицинские нано-роботы, способные ремонтировать поврежденные ткани и органы на клеточном уровне."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140875688, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Прорыв в био-инженерии» для Boston 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.