-- Quest: canon-quest-hk-star-ferry
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hk-star-ferry', 'Гонконг 2020-2029 — Паром «Звёздный»', 'Игрок переправляется между Central и Tsim Sha Tsui, наблюдая skyline Гонконга и жизнь гавани.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hk-star-ferry", "title": "Гонконг 2020-2029 — Паром «Звёздный»", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:20:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["hong_kong", "transport", "victoria_harbour"], "topics": ["heritage-transport", "cityscape"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "social-service"], "related_documents": [{"id": "github-issue-380", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/380", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-003-star-ferry.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культовая переправа Star Ferry была в Markdown и не подключалась к транспортным механикам.", "goal": "Структурировать поездку через Victoria Harbour в YAML, подчеркнув атмосферу и экономичный транспорт.", "essence": "Игрок переправляется между Central и Tsim Sha Tsui, наблюдая skyline Гонконга и жизнь гавани.", "key_points": ["Квест-id HK-2029-003, сложность easy, длительность до часа.", "Этапы включают посадку, 10-минутный маршрут, наблюдение за парусными джонками и прибытие.", "Награда выдаёт 500 XP, +10 ностальгии и титул «Пассажир Star Ferry»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Star Ferry работает с 1888 года, связывая остров Гонконг и Коулун; зелёно-белые суда дарят панорамные виды на skyline и оживлённый порт.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Купить билет и занять место на деревянных скамьях парома.\\n2. Пересечь гавань, отслеживая движение контейнеровозов и джонок.\\n3. Зафиксировать ключевые точки skyline и провести короткую фотосессию.\\n4. Прибытие в Tsim Sha Tsui и анализ туристических потоков.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны тарифы (2.70 HKD), расписание, историческая значимость маркёра «самая живописная переправа» и роль Victoria Harbour как одного из самых оживлённых портов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 3 HKD, получает 500 XP, +10 к ресурсу ностальгии и титул «Пассажир Star Ferry», открывая дополнительные задания транспортной линии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 380, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Паром «Звёздный»» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

