INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-myeongdong-shopping', 'Сеул 2020-2029 — Шопинг в Мёндоне', 'Игрок гуляет по Мёндону, закупает косметику, пробует уличную еду и возвращается с сумками.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-myeongdong-shopping", "title": "Сеул 2020-2029 — Шопинг в Мёндоне", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:35:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["seoul", "myeongdong", "k-beauty"], "topics": ["shopping", "street-food"], "related_systems": ["narrative-service", "commerce-system", "buff-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/seoul/2020-2029/quest-009-myeongdong-shopping.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест «Шопинг в Мёндоне» был в Markdown и не подключал систему K-Beauty и уличную еду.", "goal": "Перевести торговый маршрут в YAML, описав магазины, косметику и фуд-корты.", "essence": "Игрок гуляет по Мёндону, закупает косметику, пробует уличную еду и возвращается с сумками.", "key_points": ["Квест-id SEOUL-2029-009, сложность easy, длительность 1–2 часа.", "Этапы включают покупку K-Beauty, дегустацию уличной еды и изучение модных трендов.", "Награда даёт 600 XP, +10 к внешности и титул «K-Beauty эксперт»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Myeongdong — шопинг-мекка Сеула: бесконечные K-Beauty магазины, неоновые вывески, бесплатные samples и ароматная уличная еда.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Исследовать главную улицу и собрать бесплатные samples косметики.\\n2. Закупить линейки K-Beauty в Innisfree, Etude House, The Face Shop.\\n3. Попробовать уличную еду: tteokbokki, hotteok, odeng.\\n4. Приобрести модные аксессуары и оформить покупку Tax Free.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны неоновые вывески, уличные продавцы, толпы туристов, популярность sheet masks и влияние K-Beauty на мировые тренды.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 5 000 едди, получает 600 XP, +10 к параметру внешности, бафф «K-Beauty эксперт» и доступ к эксклюзивным косметическим вставкам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890426, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Шопинг в Мёндоне» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCL