INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hk-triad-initiation', 'Гонконг 2020-2029 — Инициация в триаду', 'Игрок проходит испытания, приносит кровавую клятву и получает статус «49», получая доступ к криминальным контрактам.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hk-triad-initiation", "title": "Гонконг 2020-2029 — Инициация в триаду", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:20:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["hong_kong", "triads", "organized_crime"], "topics": ["faction-alignment", "criminal-rituals"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "faction-service", "reputation-service", "social-service", "security-service"], "related_documents": [{"id": "github-issue-381", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/381", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-005-triad-initiation.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "extreme"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Ритуал вступления в триаду существовал в Markdown и не был синхронизирован с фракционными механиками и последствиями.", "goal": "Перенести инициацию триады в YAML, отразив 36 клятв, кровавый ритуал и долгосрочные обязательства.", "essence": "Игрок проходит испытания, приносит кровавую клятву и получает статус «49», получая доступ к криминальным контрактам.", "key_points": ["Квест-id HK-2029-005, сложность extreme, длительность 4+ часов.", "Этапы включают сбор дани, церемонию с благовониями, клятвами и татуировкой дракона.", "Награды выдают 10 000 XP, 30 000 HKD и доступ к триадным ресурсам, но закрепляют лояльность."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Триады («Общество Трёх Гармоний») проводят сокрытые церемонии посвящения с 36 клятвами, кровавыми ритуалами и почитанием Гуань Ди, создавая необратимую связь.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Получить приглашение через контакт и доказать полезность, собрав дань в Монг Коке.\\n2. Пройти ритуал 36 клятв, кровавую клятву и поклонение алтарю Гуань Ди.\\n3. Пережить церемонию с благовониями, красными конвертами и татуировкой дракона.\\n4. Принять статус «49», получить наставника и новые обязанности.\\n", "mechanics_links": [], "assets": []}, {"id": "decisions_outcomes", "title": "Решения и последствия", "body": "• Принять все клятвы: полный доступ к контрактам, риски при нарушении кодекса.\\n• Попытаться шпионить: шанс раскрытия и смертельные последствия.\\n• Отказаться в последнюю минуту: потеря доверия и преследование триад.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указаны основные синдикаты (14K, Sun Yee On, Wo Shing Wo), значение красных конвертов, символика тесака и выражение «一日為師，終身為父».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 10 000 XP, 30 000 HKD, защиту триады и доступ к криминальным территориям, но связывается обязательствами и угрозами со стороны полиции.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 381, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Инициация в триаду» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

