INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hk-neon-signs', 'Гонконг 2020-2029 — Неоновые вывески', 'Игрок путешествует по Монг Коку и соседним кварталам, собирая коллекцию культовых неоновых вывесок под дождём.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hk-neon-signs", "title": "Гонконг 2020-2029 — Неоновые вывески", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:25:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["hong_kong", "neon", "photography"], "topics": ["urban-aesthetics", "night-life"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "culture-service", "social-service"], "related_documents": [{"id": "github-issue-381", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/381", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-006-neon-signs.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Фотомарафон неоновых вывесок хранился в Markdown и не добавлял эстетические коллекции в систему.", "goal": "Описать ночной тур по неону Гонконга в YAML, выделив ключевые районы и результаты.", "essence": "Игрок путешествует по Монг Коку и соседним кварталам, собирая коллекцию культовых неоновых вывесок под дождём.", "key_points": ["Квест-id HK-2029-006, сложность easy, длительность 1–2 часа.", "Маршрут охватывает Mong Kok, Yau Ma Tei, Jordan и Tsim Sha Tsui.", "Награды дают 1 000 XP, фотоколлекцию из 20 вывесок и титул «Неоновый охотник»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Неоновые вывески Гонконга — символ киберпанк-эстетики: вертикальные иероглифы, красные и синие оттенки, отражения на мокром асфальте.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Совершить ночной обход ключевых улиц в Mong Kok.\\n2. Сфотографировать минимум 20 культовых вывесок, фиксируя цвета и композицию.\\n3. Использовать погодные эффекты (дождь, туман) для усиления киберпанк-настроения.\\n4. Сформировать коллекцию и загрузить её в эстетическую базу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны вертикальные китайские вывески, традиционные мастерские, уличные рынки, влияние «Blade Runner» и постепенное исчезновение неона.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 1 000 XP, коллекцию «Neon Hong Kong» (20 снимков) и титул «Неоновый охотник», повышая эстетический рейтинг города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 381, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Неоновые вывески» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

