-- Quest: canon-quest-mexico-city-zocalo-square-2020-2029
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-zocalo-square-2020-2029', 'Мехико: площадь Сокало', 'Игрок посещает Zócalo, изучает окрестные памятники и наблюдает патриотический ритуал.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "examine_zocalo_flag", "text": "Осмотреть площадь и гигантский флаг Мексики", "type": "interact", "target": "zocalo_square_flag", "count": 1, "optional": false}, {"id": "visit_historic_buildings", "text": "Посетить собор Metropolitana, Национальный дворец и смотровую платформу Templo Mayor", "type": "explore", "target": "zocalo_historic_buildings", "count": 3, "optional": false}, {"id": "attend_flag_ceremony", "text": "Присутствовать на церемонии подъёма/спуска флага и взаимодействовать с почётным караулом", "type": "interact", "target": "flag_ceremony", "count": 1, "optional": false}, {"id": "collect_audio_guide", "text": "Собрать аудиогид по истории ацтеков и колониального периода", "type": "investigate", "target": "zocalo_audio_guide", "count": 1, "optional": false}, {"id": "complete_cultural_journal", "text": "Зафиксировать впечатления в журнале культуры города", "type": "interact", "target": "cultural_journal_entry", "count": 1, "optional": false}]'::jsonb, '{"experience": 800, "money": 0, "items": [{"item_id": "achievement_zocalo_master", "quantity": 1}], "reputation": {}, "unlocks": ["heritage_quests", "zocalo_access"]}'::jsonb, '[{"branch_id": "heritage_enthusiast", "description": "Стать энтузиастом исторического наследия Zócalo", "consequences": {"skills": {"culture": 20, "history": 15}, "reputation": {"Tourism_Industry": 20}, "unlocks": ["exclusive_heritage_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Осмотреть площадь и гигантский флаг Мексики.\\n2. Посетить собор Metropolitana, Национальный дворец и смотровую платформу Templo Mayor.\\n3. Присутствовать на церемонии подъёма/спуска флага и взаимодействовать с почётным караулом.\\n4. Собрать аудиогид по истории ацтеков и колониального периода.\\n5. Зафиксировать впечатления в журнале культуры города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Zócalo — вторая по величине площадь мира, построенная на месте центра Теночтитлана.\\n- Огромный флаг размером 50×28 м меняется в ходе ежедневных церемоний.\\n- Под площадью сохранились остатки ацтекского храма Templo Mayor.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 800 XP.\\n- Финансовый баланс: 0.\\n- Культурный бонус +15.\\n- Достижение «Хозяин Сокало».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Туристические активности и исторические справки.\\n- Патриотические события с NPC и анимациями флага.\\n- Исследование многоуровневых культурных слоёв города.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест открывает линии о колониальном Мехико, ацтекских руинах и современных протестных движениях на площади.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-zocalo-square-2020-2029", "title": "Мехико: площадь Сокало", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "heritage", "quest"], "topics": ["culture", "history", "quests"], "related_systems": ["narrative-service", "quest-service", "world-simulation", "culture-service", "tourism-service", "gameplay-service", "character-service", "economy-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/mexico-city/2020-2029/quest-001-zocalo-square.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Историческая ветка Мехико не имела структурированного квеста о главной площади Зокало и её символах.", "goal": "Познакомить игрока с колониальным ядром города, ацтекским наследием и церемонией подъёма флага.", "essence": "Игрок посещает Zócalo, изучает окрестные памятники и наблюдает патриотический ритуал.", "key_points": ["Площадь объединяет Национальный дворец, собор и руины Templo Mayor.", "Гигантский флаг формирует эмоциональный центр квеста.", "Квест подчёркивает непрерывность истории от ацтеков до современности."]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игрок прибывает на площадь Конституции (Zócalo), знакомится с историческими зданиями и церемониями, которые делают\\nеё сердцем Мехико.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "examine_zocalo_flag", "text": "Осмотреть площадь и гигантский флаг Мексики", "type": "interact", "target": "zocalo_square_flag", "count": 1, "optional": false}, {"id": "visit_historic_buildings", "text": "Посетить собор Metropolitana, Национальный дворец и смотровую платформу Templo Mayor", "type": "explore", "target": "zocalo_historic_buildings", "count": 3, "optional": false}, {"id": "attend_flag_ceremony", "text": "Присутст