-- Quest: canon-quest-mexico-city-tacos-al-pastor-2020-2029
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-tacos-al-pastor-2020-2029', 'Мехико: такос аль пастор', 'Игрок ищет лучшую такерию, наблюдает за поваром у тромпо, собирает порцию и получает гастрономический бафф.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_trompo_taqueria", "text": "Найти такерию с живым тромпо и получить рекомендации от местных", "type": "interact", "target": "trompo_taqueria", "count": 1, "optional": false}, {"id": "study_cutting_technique", "text": "Изучить процесс нарезки мяса и ананаса, зафиксировать технику", "type": "investigate", "target": "trompo_cutting_technique", "count": 1, "optional": false}, {"id": "assemble_tacos", "text": "Собрать порцию с кинзой, луком, сальсой верде и лаймом", "type": "interact", "target": "tacos_al_pastor_assembly", "count": 3, "optional": false}, {"id": "evaluate_taste", "text": "Оценить вкус, записать отзывы NPC и сравнить с другими закусочными", "type": "interact", "target": "tacos_taste_evaluation", "count": 3, "optional": false}, {"id": "create_culinary_note", "text": "Сформировать гастрономическую заметку для путеводителя", "type": "interact", "target": "culinary_guide_note", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "money": -10, "items": [{"item_id": "achievement_tacos_gourmet", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "street_food_access"]}'::jsonb, '[{"branch_id": "taco_master", "description": "Стать мастером такос аль пастор", "consequences": {"skills": {"culinary": 25, "culture": 20}, "reputation": {"Street_Food_Vendors": 25}, "unlocks": ["exclusive_culinary_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Найти такерию с живым тромпо и получить рекомендации от местных.\\n2. Изучить процесс нарезки мяса и ананаса, зафиксировать технику.\\n3. Собрать порцию с кинзой, луком, сальсой верде и лаймом.\\n4. Оценить вкус, записать отзывы NPC и сравнить с другими закусочными.\\n5. Сформировать гастрономическую заметку для путеводителя.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Tacos al pastor появились благодаря ливанским мигрантам, адаптировавшим шашлык к мексиканской кухне.\\n- Ананас сверху карамелизуется и добавляется к каждому такос.\\n- Лучшие такерии размещены на улицах и работают до поздней ночи.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 600 XP.\\n- Финансовые расходы: -10.\\n- Бафф здоровья +20% на 6 часов.\\n- Достижение «Такос-Гурман».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные баффы и сбор рецептов.\\n- Социальные взаимодействия с уличными поварами.\\n- Экономическое влияние уличной еды на районы города.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест открывает линейки о гастрономических турах Мехико, фестивалях уличной еды и конкуренциях такерий.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-tacos-al-pastor-2020-2029", "title": "Мехико: такос аль пастор", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "cuisine", "quest"], "topics": ["food", "culture", "quests"], "related_systems": ["narrative-service", "quest-service", "economy-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "complement