-- Quest: canon-quest-mexico-city-teotihuacan-pyramids-2020-2029
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-teotihuacan-pyramids-2020-2029', 'Мехико: пирамиды Теотиуакана', 'Игрок отправляется за пределы Мехико, исследует пирамиды Солнца и Луны, фиксируя панорамы и археологические данные.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "reach_teotihuacan", "text": "Добраться до Теотиуакана (50 км от Мехико) и получить разрешение археологов", "type": "interact", "target": "teotihuacan_archaeological_zone", "count": 1, "optional": false}, {"id": "explore_avenue_of_the_dead", "text": "Пройти по Avenue of the Dead и изучить настенные фрески", "type": "explore", "target": "avenue_of_the_dead", "count": 1, "optional": false}, {"id": "climb_sun_pyramid", "text": "Подняться на Пирамиду Солнца (248 ступеней) и зафиксировать панораму", "type": "interact", "target": "sun_pyramid_ascent", "count": 1, "optional": false}, {"id": "visit_moon_pyramid", "text": "Посетить Пирамиду Луны и выполнить фото/сканирование рельефов", "type": "investigate", "target": "moon_pyramid_scan", "count": 1, "optional": false}, {"id": "collect_artifacts_data", "text": "Собрать артефактные данные для музея Мехико и вернуться в город", "type": "interact", "target": "archaeological_data_collection", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "money": -30, "items": [{"item_id": "achievement_teotihuacan_explorer", "quantity": 1}], "reputation": {}, "unlocks": ["archaeology_quests", "teotihuacan_access"]}'::jsonb, '[{"branch_id": "archaeological_expert", "description": "Стать экспертом археологии Теотиуакана", "consequences": {"skills": {"archaeology": 35, "history": 30}, "reputation": {"Archaeological_Community": 30}, "unlocks": ["exclusive_archaeological_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Добраться до Теотиуакана (50 км от Мехико) и получить разрешение археологов.\\n2. Пройти по Avenue of the Dead и изучить настенные фрески.\\n3. Подняться на Пирамиду Солнца (248 ступеней) и зафиксировать панораму.\\n4. Посетить Пирамиду Луны и выполнить фото/сканирование рельефов.\\n5. Собрать артефактные данные для музея Мехико и вернуться в город.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Теотиуакан достигает 65 м в высоту и является третьей по величине пирамидой мира.\\n- Город основан в I веке н.э., задолго до ацтеков, его строители до сих пор неизвестны.\\n- Панорама с вершины демонстрирует древнюю мегалополисную планировку.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 3 000 XP.\\n- Финансовые расходы: -30.\\n- Археологический бонус +25.\\n- Достижение «Исследователь Теотиуакана».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Исследовательские миссии с анализом артефактов.\\n- Системы выносливости при подъёме на пирамиды.\\n- Исторический лор и культурные записи.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест открывает доступ к археологическим экспедициям Мексики и сюжетам о доколумбовых цивилизациях.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-teotihuacan-pyramids-2020-2029", "title": "Мехико: пирамиды Теотиуакана", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "archaeology", "quest"], "topics": ["exploration", "history", "quests"], "related_systems": ["narrative-service", "quest-service", "world-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/mexico-city/2020-2029/quest-003-teotihuacan-pyramids.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Археологическая линия Мексики требовала квеста о древнем Теотиуакане, превосходящем ацтекскую эпоху.", "goal": "Провести игрока по Авеню мёртвых, поднять на пирамиды и показать значение города «где рождаются боги».", "essence": "Игрок отправляется за пределы Мехико, исследует пирамиды Солнца и Луны, фиксируя панорамы и археологические данные.", "key_points": ["Подъём по 248 ступеням Пирамиды Солнца — испытание выносливости.", "Квест подчёркивает, что город построен до ацтеков и остаётся загадкой.", "Награды усиливают археологическую репутацию и дают эмоциональный опыт."]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игрок посещает археологическую зону Теотиуакана, исследует проспект Мёртвых и поднимается на две величайшие пирамиды,\\nполучая доступ к историческим данным и сейв-поинтам.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "reach_teotihuacan", "text": "Добраться до Теотиуакана (50 км от Мехико) и получить разрешение археологов", "type": "interact", "target": "teotihuacan_archaeological_zone", "count": 1, "optional": false}, {"id": "explore_avenue_of_the_dead", "text": "Пройти по Avenue of the Dead и изучить настенные фрески", "type": "explore", "target": "avenue_of_the_dead", "count": 1, "optional": false}, {"id": "climb_sun_pyramid", "text": "Подняться на Пирамиду Солнца (248 ступеней) и зафиксировать панораму", "type": "interact", "target": "sun_pyramid_ascent", "count": 1, "optional": false}, {"id": "visit_moon_pyramid", "text": "Посетить Пирамиду Луны и выполнить фото/сканирование рельефов", "type": "investigate", "target": "moon_pyramid_scan", "count": 1, "optional": false}, {"id": "collect_artifacts_data", "text": "Собрать артефактные данные для музея Мехико и вернуться в город", "type": "interact", "target": "archaeological_data_collection", "count": 1, "optional": false}], "rewards": {"experience": 3000, "money": -30, "items": [{"item_id": "achievement_teotihuacan_explorer", "quantity": 1}], "reputation": {}, "unlocks": ["archaeology_quests", "teotihuacan_access"]}, "branches": [{"branch_id": "archaeological_expert", "description": "Стать экспертом археологии Теотиуакана", "consequences": {"skills": {"archaeology": 35, "history": 30}, "reputation": {"Archaeological_Community": 30}, "unlocks": ["exclusive_archaeological_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Добраться до Теотиуакана (50 км от Мехико) и получить разрешение археологов.\\n2. Пройти по Avenue of the Dead и изучить настенные фрески.\\n3. Подняться на Пирамиду Солнца (248 ступеней) и зафиксировать панораму.\\n4. Посетить Пирамиду Луны и выполнить фото/сканирование рельефов.\\n5. Собрать артефактные данные для музея Мехико и вернуться в город.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Теотиуакан достигает 65 м в высоту и является третьей по величине пирамидой мира.\\n- Город основан в I веке н.э., задолго до ацтеков, его строители до сих пор неизвестны.\\n- Панорама с вершины демонстрирует древнюю мегалополисную планировку.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 3 00