INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-dim-sum-breakfast', 'Шанхай 2020-2029 — Завтрак дим-сам', 'Игрок приходит утром в ресторан, заказывает чай, выбирает блюда с тележек и общается с местными за чашкой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-dim-sum-breakfast", "title": "Шанхай 2020-2029 — Завтрак дим-сам", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:40:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["shanghai", "dim_sum", "cuisine"], "topics": ["culinary-culture", "social-experience"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "wellbeing-service"], "related_documents": [{"id": "github-issue-384", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/384", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/shanghai/2020-2029/quest-006-dim-sum-breakfast.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Завтрак дим-сам был в Markdown и не подключал культурные баффы и социальные взаимодействия.", "goal": "Структурировать традиционный yum cha в YAML, акцентировать выбор блюд и взаимодействие с локалами.", "essence": "Игрок приходит утром в ресторан, заказывает чай, выбирает блюда с тележек и общается с местными за чашкой.", "key_points": ["Квест-id SHANGHAI-2029-006, сложность easy, длительность до часа.", "Подчёркиваются тележки с бамбуковыми пароварками, выбор shumai, har gow, char siu bao и egg tart.", "Награда выдаёт 600 XP, -30 едди и бафф +20% HP на 8 часов."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Завтрак дим-сам — утренний ритуал yum cha в аутентичном зале с тележками, чайниками и живым шумом посетителей.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Прибыть в ресторан к открытию и запросить столик.\\n2. Выбрать чай (жасмин, улун, пуэр) и прогреть сервиз.\\n3. Поймать тележки, собрать подборку shumai, har gow, char siu bao и egg tart.\\n4. Съесть блюда из бамбуковых пароварок, обсуждая рецепты с персоналом.\\n5. Финализировать трапезу разговором с местными и обменом контактами.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны тележки, стопки бамбуковых корзин, шума зала, постоянное доливание чая и традиция «коснуться сердца» (dim sum).\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 30 едди, получает 600 XP, бафф «Сыт и доволен» (+20% HP на 8 часов) и титул «Мастер дим-сама», открывая кулинарные цепочки Шанхая.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890430, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Завтрак дим-сам» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

