INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-lucha-libre-2020-2029', 'Мехико: шоу lucha libre', 'Игрок посещает шоу, наблюдает за акробатическими боями, поддерживает любимых лучадоров и приобретает маску.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "buy_ticket_arena_mexico", "text": "Приобрести билет и занять место в Arena México", "type": "interact", "target": "arena_mexico_ticket", "count": 1, "optional": false}, {"id": "explore_hall_history", "text": "Исследовать холл, изучить историю El Santo и Blue Demon", "type": "investigate", "target": "lucha_libre_hall_history", "count": 1, "optional": false}, {"id": "watch_technicos_vs_rudos", "text": "Наблюдать бой técnicos vs rudos, выполняя задания на поддержку команды", "type": "interact", "target": "lucha_libre_match", "count": 1, "optional": false}, {"id": "participate_fan_reactions", "text": "Принять участие в фанатских реакциях: кричалки, свист, броски конфетти", "type": "interact", "target": "fan_reactions_lucha", "count": 5, "optional": false}, {"id": "buy_lucha_mask", "text": "Купить авторскую маску и зафиксировать опыт в дневнике", "type": "interact", "target": "lucha_libre_mask", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -20, "items": [{"item_id": "achievement_lucha_libre_fan", "quantity": 1}, {"item_id": "lucha_libre_mask", "quantity": 1}], "reputation": {}, "unlocks": ["sports_quests", "lucha_libre_access"]}'::jsonb, '[{"branch_id": "lucha_libre_enthusiast", "description": "Стать энтузиастом lucha libre", "consequences": {"skills": {"sports": 25, "culture": 20}, "reputation": {"Lucha_Libre_Fans": 30}, "unlocks": ["exclusive_sports_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Приобрести билет и занять место в Arena México.\\n2. Исследовать холл, изучить историю El Santo и Blue Demon.\\n3. Наблюдать бой técnicos vs rudos, выполняя задания на поддержку команды.\\n4. Принять участие в фанатских реакциях: кричалки, свист, броски конфетти.\\n5. Купить авторскую маску и зафиксировать опыт в дневнике.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Маски символизируют личность лучадора и часто передаются по наследству.\\n- Lucha libre сочетает спорт и театр, сохраняя высокую акробатику в ринге.\\n- Поединки разделяют борцов на благородных técnicos и дерзких rudos.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP.\\n- Финансовые расходы: -20.\\n- Спортивный бонус +15.\\n- Достижение «Фанат lucha libre».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Спортивные события с участием NPC и мини-играми реакции толпы.\\n- Культурные коллекции масок и атрибутики.\\n- Экономика мероприятий: торговцы, мерч, ставки.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест ведёт к сюжетам о карьерах лучадоров, подпольных боях и международной популярности мексиканского рестлинга.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-lucha-libre-2020-2029", "title": "Мехико: шоу lucha libre", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "lucha-libre", "quest"], "topics": ["sports", "culture", "quests"], "related_systems": ["narrative-service", "quest-service", "world-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/mexico-city/2020-2029/quest-004-lucha-libre.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Спортивно-культурная линия Мехико нуждалась в квесте о легендарной lucha libre, масках и противостоянии técnicos и rudos.", "goal": "Погрузить игрока в атмосферу арены México, рассказать о традициях и позволить получить собственную маску.", "essence": "Игрок посещает шоу, наблюдает за акробатическими боями, поддерживает любимых лучадоров и приобретает маску.", "key_points": ["Маска — священная идентичность лучадора, её потеря равна поражению.", "Турнир подчёркивает театральность, акробатику и разделение на добрых/злых персонажей.", "Квест усиливает спортивную репутацию и даёт культурные бонусы."]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игрок покупает билет на шоу lucha libre в Arena México, взаимодействует с фанатами и погружается в традицию масок и\\nакробатических боёв.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "buy_ticket_arena_mexico", "text": "Приобрести билет и занять место в Arena México", "type": "interact", "target": "arena_mexico_ticket", "count": 1, "optional": false}, {"id": "explore_hall_history", "text": "Исследовать холл, изучить историю El Santo и Blue Demon", "type": "investigate", "target": "lucha_libre_hall_history", "count": 1, "optional": false}, {"id": "watch_technicos_vs_rudos", "text": "Наблюдать бой técnicos vs rudos, выполняя задания на поддержку команды", "type": "interact", "target": "lucha_libre_match", "count": 1, "optional": false}, {"id": "participate_fan_reactions", "text": "Принять участие в фанатских реакциях: кричалки, свист, броски конфетти", "type": "interact", "target": "fan_reactions_lucha", "count": 5, "optional": false}, {"id": "buy_lucha_mask", "text": "Купить авторскую маску и зафиксировать опыт в дневнике", "type": "interact", "target": "lucha_libre_mask", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -20, "items": [{"item_id": "achievement_lucha_libre_fan", "quantity": 1}, {"item_id": "lucha_libre_mask", "quantity": 1}], "reputation": {}, "unlocks": ["sports_quests", "lucha_libre_access"]}, "branches": [{"branch_id": "lucha_libre_enthusiast", "description": "Стать энтузиастом lucha libre", "consequences": {"skills": {"sports": 25, "culture": 20}, "reputation": {"Lucha_Libre_Fans": 30}, "unlocks": ["exclusive_sports_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Приобрести билет и занять место в Arena México.\\n2. Исследовать холл, изучить историю El Santo и Blue Demon.\\n3. Наблюдать бой técnicos vs rudos, выполняя задания на поддержку команды.\\n4. Принять участие в фанатских реакциях: кричалки, свист, броски конфетти.\\n5. Купить авторскую маску и зафиксировать опыт в дневнике.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Маски символизируют личность лучадора и часто передаются по наследству.\\n- Lucha libre сочетает спорт и театр, сохраняя высокую акробатику в ринге.\\n- Поединки разделяют борцов на благородных técnicos и дерзких rudos.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP.\\n- Финансовые расходы: -20.\\n- Спортивный бонус +15.\\n- Достижение «Фанат lucha libre».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Спортивные события с участием NPC и мини-играми реакции толпы.\\n- Культурные коллекции масок и атрибутики.\\n- Экономика мероприятий: торговцы, мерч, ставки.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест ведёт к сюжетам о карьерах лучадоров, подпольных боях и международной популярности мексиканского рестлинга.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1116, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики спортивного квеста, lucha libre культуры и фанатских традиций."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Создан YAML-квест «Шоу lucha libre» с акцентом на маски и фанатскую культуру."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

