INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-day-of-the-dead-2020-2029', 'Мехико: Día de Muertos', 'Игрок собирает алтари, украшает цветами и хлебом мёртвых, завершает праздник фейерверками и культурными бонусами.', 'side', 4, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "gather_ofrenda_ingredients", "text": "Собрать ингредиенты для ofrenda: фотографии, calendula, pan de muerto", "type": "interact", "target": "ofrenda_ingredients", "count": 5, "optional": false}, {"id": "decorate_altar", "text": "Украсить алтарь, разместить любимые блюда и напитки предков", "type": "interact", "target": "ofrenda_altar", "count": 1, "optional": false}, {"id": "participate_procession", "text": "Поучаствовать в шествии с образами Катрин и музыкальными процессиями", "type": "interact", "target": "catrina_procession", "count": 1, "optional": false}, {"id": "visit_cemetery", "text": "Посетить кладбище, зажечь свечи и рассказать истории о родственниках", "type": "explore", "target": "day_of_dead_cemetery", "count": 1, "optional": false}, {"id": "complete_festival", "text": "Завершить праздник салютом и совместным ужином семьи", "type": "interact", "target": "festival_celebration", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": -30, "items": [{"item_id": "achievement_dancing_with_death", "quantity": 1}], "reputation": {}, "unlocks": ["cultural_quests", "day_of_dead_access"]}'::jsonb, '[{"branch_id": "cultural_master", "description": "Стать мастером культуры Día de Muertos", "consequences": {"skills": {"culture": 30, "social": 25}, "reputation": {"Mexican_Community": 30}, "unlocks": ["exclusive_cultural_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Собрать ингредиенты для ofrenda: фотографии, calendula, pan de muerto.\\n2. Украсить алтарь, разместить любимые блюда и напитки предков.\\n3. Поучаствовать в шествии с образами Катрин и музыкальными процессиями.\\n4. Посетить кладбище, зажечь свечи и рассказать истории о родственниках.\\n5. Завершить праздник салютом и совместным ужином семьи.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Día de Muertos — радостный праздник памяти, включён в список ЮНЕСКО.\\n- Символы: La Catrina, calendula, pan de muerto.\\n- Шествия и офренды встречаются во всех районах Мехико.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 000 XP.\\n- Финансовые расходы: -30.\\n- Культурный бонус +25.\\n- Достижение «Танцующий со смертью».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Культурные события с сезонными декорациями.\\n- Эмоциональные сцены и рассказы NPC.\\n- Социальные бонусы за участие в семейных праздниках.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест ведёт к сюжетам о семейной памяти, культуре праздников и взаимодействию с духовными традициями Мексики.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-day-of-the-dead-2020-2029", "title": "Мехико: Día de Muertos", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "dia-de-muertos", "quest"], "topics": ["culture", "festivals", "quests"], "related_systems": ["narrative-service", "quest-service", "world-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}]