INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-boston-sports-tech-integration', 'Boston 2020-2029 — «Интеграция технологий в спорт»', 'Бостон как центр спортивных инноваций, где традиционные ценности спорта сталкиваются с технологическим прогрессом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-boston-sports-tech-integration", "title": "Boston 2020-2029 — «Интеграция технологий в спорт»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:51:56+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["boston", "sports", "technology", "fenway", "enhancement"], "topics": ["sports", "technology", "performance"], "related_systems": ["narrative-service", "sports-system", "reputation-system"], "related_documents": [{"id": "canon-region-america-boston-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-014-sports-tech-integration.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Red Sox экспериментируют с кибернетическими улучшениями для игроков, что вызывает споры о честности спорта.", "goal": "Игрок тестирует новые технологии на тренировках и помогает определить границы между улучшением и читерством.", "essence": "Бостон как центр спортивных инноваций, где традиционные ценности спорта сталкиваются с технологическим прогрессом.", "key_points": ["Нейроимпланты для улучшения реакции и точности.", "Дебаты о честности соревнований между командами.", "Финальный чемпионат с участием улучшенных игроков."]}, "content": {"sections": [{"id": "tech_demonstration", "title": "Демонстрация технологий", "body": "Игрок получает приглашение на тренировку Red Sox. Тренер показывает прототипы имплантов для улучшения спортивных способностей.\\n", "mechanics_links": [], "assets": []}, {"id": "fairness_debate", "title": "Дебаты о честности", "body": "Другие команды обвиняют Red Sox в нечестном преимуществе. Игрок участвует в переговорах между лигами и командами.\\n", "mechanics_links": [], "assets": []}, {"id": "neural_training", "title": "Нейронная тренировка", "body": "Мини-игра: тестирование имплантов на тренировочном поле. Игрок учится контролировать усиленные способности.\\n", "mechanics_links": [], "assets": []}, {"id": "championship_showcase", "title": "Чемпионат-витрина", "body": "Финальная игра сезона с участием игроков с имплантами. Решение о будущем технологических улучшений в спорте.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 950 XP.\\n- Издержки: −40 кредов.\\n- Бафф «Спортивный кибернетик +25% физических способностей на 14 часов».\\n- Ачивка «Честный чемпион».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Neural Sports Enhancement", "definition": "Импланты, улучшающие спортивные способности через прямое воздействие на нервную систему."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140875688, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Интеграция технологий в спорт» для Boston 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, 