INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-frida-kahlo-house-2020-2029', 'Мехико: дом Фриды Кало', 'Игрок посещает Синий Дом в Койоакане, изучает экспозиции, сад и историю Фриды и Диего.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_coyoacan_casa_azul", "text": "Прибыть в Койоакан и войти в Синий Дом", "type": "interact", "target": "casa_azul_entrance", "count": 1, "optional": false}, {"id": "examine_main_halls", "text": "Осмотреть основные залы с автопортретами и работами", "type": "explore", "target": "frida_kahlo_main_halls", "count": 5, "optional": false}, {"id": "study_personal_items", "text": "Изучить личные вещи, дневники и комнату, в которой Фрида проводила последние годы", "type": "investigate", "target": "frida_personal_artifacts", "count": 1, "optional": false}, {"id": "visit_garden", "text": "Посетить сад, отмеченный мексиканскими скульптурами и черепами", "type": "explore", "target": "casa_azul_garden", "count": 1, "optional": false}, {"id": "prepare_cultural_report", "text": "Подготовить культурный отчёт, отражающий переживания Фриды и влияние на мировое искусство", "type": "interact", "target": "frida_cultural_report", "count": 1, "optional": false}]'::jsonb, '{"experience": 1200, "money": -15, "items": [{"item_id": "achievement_frida_appreciator", "quantity": 1}], "reputation": {}, "unlocks": ["art_quests", "museum_access"]}'::jsonb, '[{"branch_id": "art_enthusiast", "description": "Стать энтузиастом искусства Фриды Кало", "consequences": {"skills": {"culture": 25, "art": 20}, "reputation": {"Art_Community": 25}, "unlocks": ["exclusive_art_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Прибыть в Койоакан и войти в Синий Дом.\\n2. Осмотреть основные залы с автопортретами и работами.\\n3. Изучить личные вещи, дневники и комнату, в которой Фрида проводила последние годы.\\n4. Посетить сад, отмеченный мексиканскими скульптурами и черепами.\\n5. Подготовить культурный отчёт, отражающий переживания Фриды и влияние на мировое искусство.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- La Casa Azul находится в районе Койоакан и является музеем с 1958 года.\\n- В доме проживали Фрида Кало и Диего Ривера, хранятся их личные вещи.\\n- Ярко-синий фасад и сад отражают мексиканскую идентичность художницы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 200 XP.\\n- Финансовые расходы: -15.\\n- Культурный бонус +20.\\n- Достижение «Ценитель Фриды».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Культурные экскурсии с аудиогидами.\\n- Эмоциональные сцены и изучение биографий.\\n- Коллекционирование артефактов для музея.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест открывает линии о мексиканском искусстве, мурализме и взаимодействии с культурными институциями города.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-frida-kahlo-house-2020-2029", "title": "Мехико: дом Фриды Кало", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "art", "quest"], "topics": ["culture", "museums", "quests"], "related_systems": ["narrative-service", "quest-service", "world-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/mexico-city/2020-2029/quest-006-frida-kahlo-house.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурное наследие Фриды Кало не было представлено в базе знаний в формате квеста.", "goal": "Показать Casa Azul, личные экспонаты и влияние боли на искусство художницы.", "essence": "Игрок посещает Синий Дом в Койоакане, изучает экспозиции, сад и историю Фриды и Диего.", "key_points": ["Экспозиции включают автопортреты и личные предметы художницы.", "Сад и архитектура демонстрируют синтез мексиканской эстетики.", "Квест раскрывает эмоциональную сторону творчества."]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игрок прибывает в Casa Azul, исследует музейные комнаты, знакомится с хроникой жизни и боли Фриды Кало\\nи взаимодействует с аудиогидами, раскрывающими её символизм.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_coyoacan_casa_azul", "text": "Прибыть в Койоакан и войти в Синий Дом", "type": "interact", "target": "casa_azul_entrance", "count": 1, "optional": false}, {"id": "examine_main_halls", "text": "Осмотреть основные залы с автопортретами и работами", "type": "explore", "target": "frida_kahlo_main_halls", "count": 5, "optional": false}, {"id": "study_personal_items", "text": "Изучить личные вещи, дневники и комнату, в которой Фрида проводила последние годы", "type": "investigate", "target": "frida_personal_artifacts", "count": 1, "optional": false}, {"id": "visit_garden", "text": "Посетить сад, отмеченный