INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-mariachi-music-2020-2029', 'Мехико: музыка марьячи', 'Игрок посещает площадь, выбирает ансамбль, заказывает «Cielito Lindo» и делится впечатлениями о музыке.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_plaza_garibaldi", "text": "Вечером прибыть на Plaza Garibaldi и изучить доступные ансамбли", "type": "interact", "target": "plaza_garibaldi_evening", "count": 1, "optional": false}, {"id": "choose_mariachi_group", "text": "Выбрать группу марьячи, обсудить репертуар и оплату", "type": "interact", "target": "mariachi_group_selection", "count": 1, "optional": false}, {"id": "request_song", "text": "Заказать песню (например, «Cielito Lindo» или романтическую балладу)", "type": "interact", "target": "mariachi_song_request", "count": 1, "optional": false}, {"id": "watch_performance", "text": "Смотреть выступление, взаимодействовать с NPC и танцующими парами", "type": "interact", "target": "mariachi_performance", "count": 5, "optional": false}, {"id": "complete_cultural_note", "text": "Закрепить культурную заметку и рекомендации для будущих гостей", "type": "interact", "target": "mariachi_cultural_note", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "money": -30, "items": [{"item_id": "achievement_mariachi_appreciator", "quantity": 1}], "reputation": {}, "unlocks": ["music_quests", "mariachi_access"]}'::jsonb, '[{"branch_id": "mariachi_enthusiast", "description": "Стать энтузиастом музыки марьячи", "consequences": {"skills": {"culture": 20, "music": 20}, "reputation": {"Music_Community": 25}, "unlocks": ["exclusive_music_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Вечером прибыть на Plaza Garibaldi и изучить доступные ансамбли.\\n2. Выбрать группу марьячи, обсудить репертуар и оплату.\\n3. Заказать песню (например, «Cielito Lindo» или романтическую балладу).\\n4. Смотреть выступление, взаимодействовать с NPC и танцующими парами.\\n5. Закрепить культурную заметку и рекомендации для будущих гостей.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Plaza Garibaldi — исторический центр марьячи в Мехико.\\n- Музыканты носят костюмы чарро с серебряной отделкой.\\n- Репертуар включает «Cielito Lindo», «La Bamba» и романтические серенады.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 000 XP.\\n- Финансовые расходы: -30.\\n- Культурный бонус +15.\\n- Достижение «Ценитель марьячи».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Социальные события с наймом NPC.\\n- Музыкальные интеракции и выбор песен.\\n- Экономика чаевых и поддержки уличных артистов.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест ведёт к сюжетам о мексиканских музыкантах, романтических линиях и фестивалях музыки.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-mariachi-music-2020-2029", "title": "Мехико: музыка марьячи", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "mariachi", "quest"], "topics": ["music", "social", "quests"], "related_systems": ["narrative-service", "quest-service", "economy-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/time