INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-nanjing-road', 'Шанхай 2020-2029 — Нанкинская дорога', 'Игрок проходит East Nanjing Road, делает покупки, пробует уличную еду и фиксирует неон.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-nanjing-road", "title": "Шанхай 2020-2029 — Нанкинская дорога", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:40:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["shanghai", "nanjing_road", "shopping"], "topics": ["urban-lifestyle", "neon-atmosphere"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "market-system", "social-service", "culture-service"], "related_documents": [{"id": "github-issue-385", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/385", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/shanghai/2020-2029/quest-010-nanjing-road.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Шопинг на Nanjing Road был в Markdown и не подключал экономику и неоновую атмосферу.", "goal": "Описать торговый маршрут в YAML, подчеркнуть витрины, уличную еду и завершение у Бунда.", "essence": "Игрок проходит East Nanjing Road, делает покупки, пробует уличную еду и фиксирует неон.", "key_points": ["Квест-id SHANGHAI-2029-010, сложность easy, длительность 1–2 часа.", "Указывается длина улицы 5.5 км, пешеходная зона и киберпанк-подсветка.", "Награда 600 XP, -2 000 едди, +10 к стилю и титул «Король Нанкин Роуд»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Nanjing Road — торговая артерия Шанхая с пешеходной зоной, неоном и сотнями магазинов; вечером превращается в киберпанк-коридор.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Войти на East Nanjing Road, изучить витрины и установить бюджет.\\n2. Посетить торговые центры, приобрести бренды и местные сувениры.\\n3. Попробовать уличные блюда (shengjianbao, jianbing), записать вкусовые заметки.\\n4. Вечером зафиксировать неоновые вывески, сделать фотосессию.\\n5. Завершить маршрут у Бунда, сравнив старую и новую архитектуру.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны светящиеся иероглифы, толпы покупателей, ярмарочные тележки, звуки уличных продавцов и связь улицы с историей торговли.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 2 000 едди, получает 600 XP, +10 к стилю и титул «Король Нанкин Роуд», открывая дополнительные торговые цепочки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890430, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Нанкинская дорога» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

