INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-tequila-mezcal-2020-2029', 'Мехико: дегустация текилы и мескаля', 'Игрок посещает мезкалерию, пробует разные сорта агавовых напитков и получает социальный бафф.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_agave_establishment", "text": "Найти заведение, специализирующееся на агавовых напитках", "type": "interact", "target": "mezcaleria_bar", "count": 1, "optional": false}, {"id": "conduct_tasting", "text": "Провести дегустацию: tequila blanco, reposado и дымный мескаль", "type": "interact", "target": "tequila_mezcal_tasting", "count": 3, "optional": false}, {"id": "study_worm_legend", "text": "Изучить технологию и легенды о «червяке» в бутылке", "type": "investigate", "target": "mezcal_worm_legend", "count": 1, "optional": false}, {"id": "perform_ritual", "text": "Пройти ритуал с солью, лаймом и обменом тостами с NPC", "type": "interact", "target": "tequila_ritual", "count": 5, "optional": false}, {"id": "decide_on_worm", "text": "Принять решение о поедании червяка и зафиксировать реакцию персонажа", "type": "interact", "target": "mezcal_worm_decision", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -40, "items": [{"item_id": "achievement_mezcalero", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "agave_access"]}'::jsonb, '[{"branch_id": "agave_expert", "description": "Стать экспертом агавовых напитков", "consequences": {"skills": {"culinary": 25, "culture": 20}, "reputation": {"Bar_Community": 25}, "unlocks": ["exclusive_culinary_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Найти заведение, специализирующееся на агавовых напитках.\\n2. Провести дегустацию: tequila blanco, reposado и дымный мескаль.\\n3. Изучить технологию и легенды о «червяке» в бутылке.\\n4. Пройти ритуал с солью, лаймом и обменом тостами с NPC.\\n5. Принять решение о поедании червяка и зафиксировать реакцию персонажа.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Текила защищена происхождением (регион Халиско).\\n- Мескаль производится в основном в Оахаке и отличается дымным вкусом.\\n- Ритуал «червяка» не обязателен, но подкрепляет имидж напитка.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP.\\n- Финансовые расходы: -40.\\n- Бафф «Charisma +20%» на 4 часа.\\n- Достижение «Мескалеро».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные/барные события с баффами.\\n- Социальные мини-игры и рассказы о происхождении напитков.\\n- Экстремальный выбор с «червяком».\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест открывает ветку про фестивали агавы, путешествия по Халиско и Оахаке, а также элитные дегустации для VIP.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-tequila-mezcal-2020-2029", "title": "Мехико: дегустация текилы и мескаля", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "tequila", "mezcal", "quest"], "topics": ["gastronomy", "culture", "quests"], "related_systems": ["narrative-service", "quest-service", "economy-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/mexico-city/2020-2029/quest-008-tequila-mezcal.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Алкогольная линия Мехико не отражала традиции дегустации текилы и мескаля и их региональные особенности.", "goal": "Познакомить игрока с разницей между напитками, ритуалами дегустации и легендой о «червяке».", "essence": "Игрок посещает мезкалерию, пробует разные сорта агавовых напитков и получает социальный бафф.", "key_points": ["Текила из голубой агавы (Халиско), мескаль — из разных видов (Оахака).", "Ритуал с солью, лаймом и опциональным «червяком».", "Квест затрагивает традиции и экономику баров."]}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Игрок исследует модную мезкалерию в Мехико, пробует разновидности текилы и мескаля, узнаёт об их происхождении и\\nритуалах употребления.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_agave_establishment", "text": "Найти заведение, специализирующееся на агавовых напитках", "type": "interact", "target": "mezcaleria_bar", "count": 1, "optional": false}, {"id": "conduct_tasting", "text": "Провести дегустацию: tequila blanco, reposado и дымный мескаль", "type": "interact", "target": "tequila_mezcal_tasting", "count": 3, "optional": false}, {"id": "study_worm_legend", "text": "Изучить технологию и легенды о «червяке» в бутылке", "type": "investigate", "target": "mezcal_worm_legend", "count": 1, "optional": false}, {"id": "perform_ritual", "text": "Пройти ритуал с солью, лаймом и обменом тостами с NPC", "type": "interact", "target": "tequila_ritual", "count": 5, "optional": false}, {"id": "decide_on_worm", "text": "Принять решение о поедании червяка и зафиксировать реакцию персонажа", "type": "interact", "target": "mezcal_worm_decision", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -40, "items": [{"item_id": "achievement_mezcalero", "quantity": 1}], "reputation": {}, "unlocks": ["culinary_quests", "agave_access"]}, "branches": [{"branch_id": "agave_expert", "description": "Стать экспертом агавовых напитков", "consequences": {"skills": {"culinary": 25, "culture": 20}, "reputation": {"Bar_Community": 25}, "unlocks": ["exclusive_culinary_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Найти заведение, специализирующееся на агавовых напитках.\\n2. Провести дегустацию: tequila blanco, reposado и дымный мескаль.\\n3. Изучить технологию и легенды о «червяке» в бутылке.\\n4. Пройти ритуал с солью, лаймом и обменом тостами с NPC.\\n5. Принять решение о поедании червяка и зафиксировать реакцию персонажа.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Текила защищена происхождением (регион Халиско).\\n- Мескаль производится в основном в Оахаке и отличается дымным вкусом.\\n- Ритуал «червяка» не обязателен, но подкрепляет имидж напитка.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP.\\n- Финансовые расходы: -40.\\n- Бафф «Charisma +20%» на 4 часа.\\n- Достижение «Мескалеро».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные/барные события с баффами.\\n- Социальные мини-игры и рассказы о происхождении напитков.\\n- Экстремальный выбор с «червяком».\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест открывает ветку про фестивали агавы, путешествия по Халиско и Оахаке, а также элитные дегустации для VIP.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1120, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики кулинарного квеста, дегустации и культурных ритуалов."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Создан YAML-квест «Текила и мескаль» с ритуалами дегустации и социальными баффами."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

