INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-singapore-gardens-by-the-bay', 'Сингапур 2020-2029 — Сады у Залива', 'Игрок проходит через вертикальные сады, поднимается на мост OCBC, исследует биокупола и посещает вечернее шоу Garden Rhapsody.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-singapore-gardens-by-the-bay", "title": "Сингапур 2020-2029 — Сады у Залива", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:45:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["singapore", "gardens_by_the_bay", "supertrees"], "topics": ["eco-architecture", "immersive-tourism"], "related_systems": ["narrative-service", "ecology-system", "tourism-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/singapore/2020-2029/quest-002-gardens-by-the-bay.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Материал по Gardens by the Bay находился в Markdown и не включал экологические и световые механики.", "goal": "Формализовать посещение Supertrees, Cloud Forest и Flower Dome в YAML формате знаний.", "essence": "Игрок проходит через вертикальные сады, поднимается на мост OCBC, исследует биокупола и посещает вечернее шоу Garden Rhapsody.", "key_points": ["Квест-id SINGAPORE-2029-002, сложность easy, длительность 2–4 часа.", "Упор на Supertrees высотой 25–50 м, Cloud Forest с водопадом 35 м и энергосберегающие технологии.", "Награда 1 500 XP, -20 едди, +20 к природному ресурсу и титул «Хранитель Supertrees»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Gardens by the Bay совмещает футуристические Supertrees, биокупола и устойчивые технологии, показывая синтез природы и урбанизма.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Войти в парк и изучить план садов.\\n2. Исследовать Supertrees, подняться на мост OCBC Skyway на высоте 22 метров.\\n3. Посетить Cloud Forest, пройти вокруг водопада 35 метров и собрать данные о климат-контроле.\\n4. Заглянуть в Flower Dome, зафиксировать редкие растения и их артефакты.\\n5. Вернуться вечером на шоу Garden Rhapsody и записать светомузыкальные паттерны.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Указывается площадь 101 гектар, солнечные панели на Supertrees, автоматические системы полива и климатические зоны в биокуполах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 20 едди, получает 1 500 XP, +20 к природному ресурсу и титул «Хранитель Supertrees», открывая экологические ветки Сингапура.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890432, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Сады у Залива» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "