-- Quest: canon-quest-mexico-city-altitude-sickness-2020-2029
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-altitude-sickness-2020-2029', 'Мехико: высотная акклиматизация', 'Персонаж прилетает в город, испытывает симптомы, выполняет рекомендации и получает бафф выносливости.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_city_checkup", "text": "Приезд в город и первичное обследование состояния персонажа", "type": "interact", "target": "mexico_city_arrival_checkup", "count": 1, "optional": false}, {"id": "track_symptoms", "text": "Отслеживание симптомов: головная боль, головокружение, одышка", "type": "investigate", "target": "altitude_sickness_symptoms", "count": 3, "optional": false}, {"id": "follow_doctor_recommendations", "text": "Выполнение рекомендаций врача: гидратация, отдых, избегание алкоголя", "type": "interact", "target": "doctor_acclimatization_advice", "count": 3, "optional": false}, {"id": "light_exercises_adaptation", "text": "Лёгкие упражнения и контроль адаптации в течение 2–3 дней", "type": "interact", "target": "acclimatization_exercises", "count": 3, "optional": false}, {"id": "complete_acclimatization", "text": "Завершение квеста с фиксацией повышенной выносливости", "type": "interact", "target": "acclimatization_completion", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": 0, "items": [{"item_id": "achievement_altitude_conqueror", "quantity": 1}], "reputation": {}, "unlocks": ["survival_quests", "altitude_access"]}'::jsonb, '[{"branch_id": "altitude_expert", "description": "Стать экспертом выживания на высоте", "consequences": {"skills": {"survival": 30, "endurance": 25}, "reputation": {"Medical_Community": 20}, "unlocks": ["exclusive_survival_access"]}}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Приезд в город и первичное обследование состояния персонажа.\\n2. Отслеживание симптомов: головная боль, головокружение, одышка.\\n3. Выполнение рекомендаций врача: гидратация, отдых, избегание алкоголя.\\n4. Лёгкие упражнения и контроль адаптации в течение 2–3 дней.\\n5. Завершение квеста с фиксацией повышенной выносливости.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Высота Мехико — 2 240 м над уровнем моря.\\n- Soroche (высотная болезнь) знакома туристам и новичкам.\\n- Медицинские рекомендации включают адаптацию в первые дни.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 000 XP.\\n- Финансовые расходы: 0.\\n- Выносливость +20.\\n- Временный дебафф «Stamina -30%» в первые три дня.\\n- Достижение «Покоритель высоты».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Системы выживания и физиологические проверки.\\n- Мониторинг состояния персонажа на высоте.\\n- Обучающие подсказки о здоровье.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест связывает физиологическую ветку Мехико с сюжетами о медицине и спортивными испытаниями на высоте.\\n", "mechanics_links": [], "assets": []}]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-altitude-sickness-2020-2029", "title": "Мехико: высотная акклиматизация", "document_type": "canon", "category": "quests", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["mexico-city", "altitude", "quest"], "topics": ["survival", "physiology", "quests"], "related_systems": ["narrative-service", "quest-service", "world-service"], "related_documents": [{"id": "canon-lore-regions-a