INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hk-rooftop-parkour', 'Гонконг 2020-2029 — Паркур по крышам', 'Игрок преодолевает десять крыш подряд, завершая прыжком на IFC Tower и избегая провала.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hk-rooftop-parkour", "title": "Гонконг 2020-2029 — Паркур по крышам", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:25:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["hong_kong", "parkour", "rooftops"], "topics": ["extreme-sports", "vertical-city"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "exploration-service", "survival-service", "stealth-service"], "related_documents": [{"id": "github-issue-382", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/382", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-009-rooftop-parkour.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "extreme"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экстремальный паркур по крышам был описан в Markdown и не синхронизирован с системой вертикального перемещения и рисков.", "goal": "Описать маршрут по крышам Гонконга в YAML, учитывая высоту и последствия падения.", "essence": "Игрок преодолевает десять крыш подряд, завершая прыжком на IFC Tower и избегая провала.", "key_points": ["Квест-id HK-2029-009, сложность extreme, длительность 2–4 часа.", "Маршрут требует доступа к крышам, точных прыжков и контроля ресурсов выносливости.", "Награда выдаёт 5 000 XP и титул «Король крыш», при высоком риске смерти."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Гонконг славится плотной застройкой; крыши небоскрёбов расположены близко, что делает город естественной ареной для паркура.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Проникнуть на стартовую крышу и подготовить страховку.\\n2. Совершить серию из десяти прыжков без касания земли, используя балконы и рекламные конструкции.\\n3. Завершить маршрут финальным прыжком на IFC Tower, зафиксировать панорамы гавани.\\n4. Эвакуироваться до прибытия охраны или дождя.\\n", "mechanics_links": [], "assets": []}, {"id": "risk_management", "title": "Управление рисками", "body": "Описаны проверки на срыв, влияние ветра и дождя, использование страховочных тросов и последствия падения.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 5 000 XP, титул «Король крыш» и уникальные маршруты паркура; при падении — мгновенная гибель и потеря прогресса.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 382, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Паркур по крышам» в YAML и завершение цикла Concept 