INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-singapore-little-india-chinatown', 'Сингапур 2020-2029 — Маленькая Индия и Чайнатаун', 'Игрок проходит через три квартала, изучает храмы, рынки и уличную жизнь, собирая материалы о мультикультурализме.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-singapore-little-india-chinatown", "title": "Сингапур 2020-2029 — Маленькая Индия и Чайнатаун", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:45:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["singapore", "multicultural", "districts"], "topics": ["cultural-exploration", "urban-heritage"], "related_systems": ["narrative-service", "tourism-system", "reputation-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/singapore/2020-2029/quest-005-little-india-chinatown.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут по этническим кварталам хранился в Markdown и не фиксировал мультикультурные взаимодействия и ресурсы.", "goal": "Перевести исследование Little India, Chinatown и Kampong Glam в YAML, подчеркнув гармонию культур.", "essence": "Игрок проходит через три квартала, изучает храмы, рынки и уличную жизнь, собирая материалы о мультикультурализме.", "key_points": ["Квест-id SINGAPORE-2029-005, сложность easy, длительность 2–4 часа.", "Описаны ароматы специй, шёлковые сари, красные фонари и золотой купол Султанской мечети.", "Награда 1 500 XP, -30 едди, +20 к культурному ресурсу и титул «Мультикультурный исследователь»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест демонстрирует сингапурскую модель мультикультурности через путешествие по Little India, Chinatown и Kampong Glam.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Посетить Little India, записать ароматы специй, рынок с сари и индуистский храм.\\n2. Переместиться в Chinatown, дегустировать блюда и исследовать буддийский храм с красными фонарями.\\n3. Завершить маршрут в Kampong Glam, отметить золотой купол Султанской мечети и арабскую улицу.\\n4. Сравнить взаимодействие культур и сделать вывод о гармонии соседства.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Приводятся демографические пропорции (китайцы 74%, малайцы 13%, индийцы 9%), особенности каждого квартала и государственная поддержка мультикультурализма.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 30 едди, получает 1 500 XP, +20 к культурному ресурсу и титул «Мультикультурный исследователь», открывая дипломатические ветки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890432, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Маленькая Индия и Чайнатаун» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

