INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-bund-walk', 'Шанхай 2020-2029 — Прогулка по Бунду', 'Игрок проходит 1.5 км вдоль Бунда, изучает здания HSBC и Peace Hotel, переправляется в Пудун и сравнивает старый и новый Шанхай.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-bund-walk", "title": "Шанхай 2020-2029 — Прогулка по Бунду", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:10:00+00:00", "github_issue_number": 383, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["shanghai", "bund", "skyline"], "topics": ["urban-history", "waterfront"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "culture-service", "social-service"], "related_documents": [{"id": "github-issue-383", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/383", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/shanghai/2020-2029/quest-001-bund-walk.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Прогулка по Бунду была в Markdown и не активировала туристические маршруты и контраст стилей.", "goal": "Описать маршрут вдоль набережной в YAML, подчеркнуть историю колониальных зданий и виды на Пудун.", "essence": "Игрок проходит 1.5 км вдоль Бунда, изучает здания HSBC и Peace Hotel, переправляется в Пудун и сравнивает старый и новый Шанхай.", "key_points": ["Квест-id SHANGHAI-2029-001, сложность easy, длительность 1–2 часа.", "Маршрут сочетает исторические здания HSBC, Peace Hotel, Customs House и виды на небоскрёбы Пудуна.", "Награда выдаёт 800 XP, +15 к эстетическому ресурсу и титул «Прогулка по времени»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "The Bund — набережная Хуанпу с европейскими фасадами 1920-х годов; напротив — футуристический Пудун с Oriental Pearl Tower и Shanghai Tower.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Пройти от пирса до Mariners Club, изучая исторические фасады.\\n2. Зайти в HSBC Building, Peace Hotel, Customs House и собрать краткие досье.\\n3. Переправиться на пароме к Пудуну и сделать серию снимков контраста.\\n4. Завершить маршрут вечерней прогулкой, фиксируя неоновую подсветку.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны европейские фасады, часы Customs House, неон Бунда вечером, панорама «трёх башен» Пудуна и прозвище «Париж Востока».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 800 XP, +15 к эстетическому ресурсу и титул «Прогулка по времени», открывая дополнительные экскурсии по колониальному наследию.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 383, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Прогулка по Бунду» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::