INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-boston-urban-farming-revolution', 'Boston 2020-2029 — «Революция городского фермерства»', 'Городское фермерство как решение экологических проблем, где высокие технологии встречаются с традиционным сельским хозяйством.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-boston-urban-farming-revolution", "title": "Boston 2020-2029 — «Революция городского фермерства»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:51:56+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["boston", "farming", "urban", "sustainability", "technology"], "topics": ["agriculture", "sustainability", "technology"], "related_systems": ["narrative-service", "economy-system", "sustainability-system"], "related_documents": [{"id": "canon-region-america-boston-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-015-urban-farming-revolution.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Бостон внедряет вертикальное фермерство с использованием генной инженерии для решения проблемы продовольственной безопасности.", "goal": "Игрок помогает запустить инновационную ферму на крышах города и преодолеть сопротивление традиционных фермеров.", "essence": "Городское фермерство как решение экологических проблем, где высокие технологии встречаются с традиционным сельским хозяйством.", "key_points": ["Вертикальные фермы с генетически модифицированными культурами.", "Конфликт с традиционными фермерами из Новой Англии.", "Финальная продовольственная выставка с новыми продуктами."]}, "content": {"sections": [{"id": "farming_innovation", "title": "Фермерская инновация", "body": "Игрок посещает экспериментальную ферму на крыше небоскреба в Financial District. Ученые демонстрируют вертикальное выращивание с ИИ-оптимизацией.\\n", "mechanics_links": [], "assets": []}, {"id": "traditional_resistance", "title": "Сопротивление традиций", "body": "Фермеры из окрестных районов протестуют против \\"неестественной еды\\". Игрок организует встречу между сторонами.\\n", "mechanics_links": [], "assets": []}, {"id": "genetic_optimization", "title": "Генетическая оптимизация", "body": "Мини-игра: настройка генов растений для максимальной урожайности. Баланс между питательностью и устойчивостью к болезням.\\n", "mechanics_links": [], "assets": []}, {"id": "harvest_festival", "title": "Фестиваль урожая", "body": "Финальная выставка новых продуктов питания. Игрок презентует достижения городской фермы представителям пищевой индустрии.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 900 XP.\\n- Издержки: −35 кредов.\\n- Бафф «Городской фермер +20% эффективности в выживании на 13 часов».\\n- Ачивка «Зеленый новатор».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Vertical Farming", "definition": "Метод выращивания растений в многоуровневых конструкциях в городской среде с использованием гидропоники и автоматизации."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140875688, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Революция городского фермерства» для Boston 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1