INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-miami-2029-everglades-gators', 'Майами 2020-2029 — Аллигаторы Эверглейдс', 'Команда управляет лодкой на воздушной подушке, исследует болота и принимает решения о взаимодействии с аллигаторами.', 'side', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "get_airboat", "text": "Организовать экскурсию на airboat в Эверглейдс", "type": "interact", "target": "everglades_tour_office", "count": 1, "optional": false}, {"id": "explore_swamps", "text": "Исследовать болота на воздушной лодке", "type": "interact", "target": "everglades_swamps", "count": 1, "optional": false}, {"id": "encounter_alligators", "text": "Встретиться с аллигаторами и принять решение об охоте или документировании", "type": "choice", "target": "alligator_encounter", "count": 1, "optional": false}, {"id": "survive_night", "text": "Пережить ночной шторм в болотах", "type": "interact", "target": "everglades_night_survival", "count": 1, "optional": false}]'::jsonb, '{"xp": 1500, "currency": -100, "attributes": {"survival": 15, "nature": 10}, "achievements": [{"id": "swamp_survivor", "name": "Swamp Survivor"}], "reputation": {}, "items": []}'::jsonb, '[{"condition": "Охота на аллигаторов", "outcome": "Дополнительные ресурсы, снижение репутации с защитниками природы", "next_quests": []}, {"condition": "Документирование", "outcome": "Увеличение репутации с защитниками природы, исследовательский бонус", "next_quests": []}]'::jsonb, '{"metadata": {"id": "quest-miami-2029-everglades-gators", "title": "Майами 2020-2029 — Аллигаторы Эверглейдс", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["miami", "everglades", "survival"], "topics": ["wilderness_exploration", "cooperative_play"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "survival-service", "exploration-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1108", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1108", "relation": "migrated_to", "migrated_at": "2025-11-23T12:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/miami/2020-2029/quest-006-everglades-gators.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природной ветке Майами не хватало экспедиции, раскрывающей опасность и красоту Эверглейдс.", "goal": "Дать игрокам кооперативный опыт с управлением airboat и выбором отношения к дикой природе.", "essence": "Команда управляет лодкой на воздушной подушке, исследует болота и принимает решения о взаимодействии с аллигаторами.", "key_points": ["Демонстрируется биом «River of Grass» и культура гидов национального парка.", "Система угроз включает нападения аллигаторов, комаров и ночные штормы.", "Результат миссии меняется в зависимости от решения охотиться или документировать."]}, "quest_definition": {"quest_type": "side", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "get_airboat", "text": "Организовать экскурсию на airboat в Эверглейдс", "type": "interact", "target": "everglades_tour_office", "count": 1, "optional": false}, {"id": "explore_swamps", "text": "Исследовать болота на воздушной лодке", "type": "interact", "target": "everglades_swamps", "count": 1, "optional": false}, {"id": "encounter_alligators", "text": "Встретиться с аллигаторами и принять решение об охоте или документировании", "type": "choice", "target": "alligator_encounter", "count": 1, "optional": false}, {"id": "survive_night", "text": "Пережить ночной шторм в болотах", "type": "interact", "target": "everglades_night_survival", "count": 1, "optional": false}], "rewards": {"xp": 1500, "currency": -100, "attributes": {"survival": 15, "nature": 10}, "achievements": [{"id": "swamp_survivor", "name": "Swamp Survivor"}], "reputation": {}, "items": []}, "branches": [{"condition": "Охота на аллигаторов", "outcome": "Дополнительные ресурсы, снижение репутации с защитниками природы", "next_quests": []}, {"condition": "Документирование", "outcome": "Увеличение репутации с защитниками природы, исследовательский бонус", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит группу игроков в Everglades National Park, где шумные airboat-туры сочетаются с постоянной опасностью\\nсо стороны хищной фауны и меняющейся погоды.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Нанять лицензированного гида и собрать снаряжение для болотной экспедиции.\\n2. Пройти инструктаж по управлению airboat и экстренным протоколам.\\n3. Исследовать мангровые тоннели, отслеживая аллигаторов и редких птиц.\\n4. Принять решение: провести охоту ради ресурсов или ограничиться фото-документацией.\\n5. Переждать ночь в полевой хижине, отражая нападения и завершая миссию.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Эверглейдс называют «River of Grass» из-за медленного течения и sawgrass.\\n- Airboat с огромным вентилятором позволяет двигаться по мелководью и густой траве.\\n- В регионе обитают тысячи аллигаторов, а также редкая флоридская пантера и колонии птиц.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP.\\n- Финансы: 0.\\n- Атрибут «Природа» +15.\\n- Ачивка «Укротитель аллигаторов».\\n- Шанс на редкие биоматериалы.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Управление airboat с учетом шума и манёвров.\\n- Система выживания: жара, комары, ограниченные ресурсы.\\n- Кооперативные решения и распределение ролей в группе 2-4 игроков.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест ведёт к сюжетам о защите экосистемы, браконьерстве и климатических угрозах в прибрежной Флориде.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1108, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Аллигаторы Эверглейдс» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

