INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-dmz-expedition', 'Сеул 2020-2029 — Экспедиция в DMZ', 'Игрок нелегально проникает в DMZ, пересекает минные поля и извлекает артефакт Корейской войны.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-dmz-expedition", "title": "Сеул 2020-2029 — Экспедиция в DMZ", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:30:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["seoul", "dmz", "infiltration"], "topics": ["geopolitical-risk", "stealth-operations"], "related_systems": ["narrative-service", "stealth-system", "survival-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/seoul/2020-2029/quest-005-dmz-expedition.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "extreme"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание экспедиции в DMZ находилось в Markdown и не подключало стелс-механику и политические последствия.", "goal": "Структурировать миссию в YAML, отразив минные поля, встречу с дронами и эвакуацию.", "essence": "Игрок нелегально проникает в DMZ, пересекает минные поля и извлекает артефакт Корейской войны.", "key_points": ["Квест-id SEOUL-2029-005, сложность extreme, формат group 2-4, длительность 4+ часов.", "Этапы охватывают проникновение, движение по минным полям, встречу с дронами и эвакуацию.", "Награда выдаёт 15 000 XP, 100 000 едди и артефакты войны при риске гибели или ареста."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "DMZ — демилитаризованная зона между Кореями: миллионы мин, колючая проволока, вышки и удивительно богатая дикая природа из-за отсутствия людей.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Получить нелегальный доступ через подпольных проводников.\\n2. Пересечь минные поля, используя карты, детекторы и стелс.\\n3. Уклониться от северокорейских дронов и патрулей, используя укрытия.\\n4. Найти артефакт времён Корейской войны и провести анализ.\\n5. Эвакуироваться до обнаружения обеими сторонами.\\n", "mechanics_links": [], "assets": []}, {"id": "risk_management", "title": "Риски и политика", "body": "Указаны последствия попадания на мины, ареста северо- или южнокорейскими войсками, дипломатические риски и необходимость бесшумной связи.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 15 000 XP, 100 000 едди и артефакт Корейской войны; репутация среди разведсообщества растёт, но усиливается риск политического конфликта.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890426, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Экспедиция в DMZ» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

