INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-miami-2029-speedboat-race', 'Майами 2020-2029 — Гонки на спидботах', 'Персонаж регистрируется в гонке, тюнингует лодку, преодолевает 10 кругов и соперников среди неоновых огней.', 'side', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "tune_speedboat", "text": "Тюнинговать спидбот для гонки", "type": "interact", "target": "speedboat_tuning_shop", "count": 1, "optional": false}, {"id": "join_race", "text": "Принять участие в подпольной гонке на спидботах в Biscayne Bay", "type": "interact", "target": "speedboat_race_event", "count": 1, "optional": false}, {"id": "avoid_police", "text": "Избежать полицейского рейда во время гонки", "type": "interact", "target": "police_raid_avoidance", "count": 1, "optional": false}, {"id": "win_race", "text": "Выиграть гонку или завершить её успешно", "type": "interact", "target": "race_finish", "count": 1, "optional": false}]'::jsonb, '{"xp": 2000, "currency": 5000, "attributes": {"driving": 20, "courage": 15}, "achievements": [{"id": "speedboat_legend", "name": "Speedboat Legend"}], "reputation": {}, "items": [{"id": "legendary_speedboat", "name": "Легендарный спидбот", "count": 1}]}'::jsonb, '[{"condition": "Победа в гонке", "outcome": "Полное вознаграждение и легендарный спидбот", "next_quests": []}, {"condition": "Финиш без победы", "outcome": "Уменьшенное вознаграждение, нет спидбота", "next_quests": []}]'::jsonb, '{"metadata": {"id": "quest-miami-2029-speedboat-race", "title": "Майами 2020-2029 — Гонки на спидботах", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["miami", "speedboat", "adrenaline"], "topics": ["racing", "high_risk_events"], "related_systems": ["narrative-service", "liveops-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/miami/2020-2029/quest-008-speedboat-race.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Морской ветке Майами не хватало экшен-квеста с подпольными гонками и высоким риском.", "goal": "Провести игрока через подготовку к спидбот-турниру и показать культуру Biscayne Bay.", "essence": "Персонаж регистрируется в гонке, тюнингует лодку, преодолевает 10 кругов и соперников среди неоновых огней.", "key_points": ["Biscayne Bay и cigarette boats создают фирменный стиль Майами.", "Упор делается на тюнинг, реакцию и управление волнами.", "Победа приносит крупный приз и легендарный спидбот."]}, "quest_definition": {"quest_type": "side", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "tune_speedboat", "text": "Тюнинговать спидбот для гонки", "type": "interact", "target": "speedboat_tuning_shop", "count": 1, "optional": false}, {"id": "join_race", "text": "Принять участие в подпольной гонке на спидботах в Biscayne Bay", "type": "interact", "target": "speedboat_race_event", "count": 1, "optional": false}, {"id": "avoid_police", "text": "Избежать полицейского рейда во время гонки", "type": "interact", "target": "police_raid_avoidance", "count": 1, "optional": false}, {"id": "win_race", "text": "Выиграть гонку или завершить её успешно", "type": "interact", "target": "race_finish", "count": 1, "optional": false}], "rewards": {"xp": 2000, "currency": 5000, "attributes": {"driving": 20, "courage": 15}, "achievements": [{"id": "speedboat_legend", "name": "Speedboat Legend"}], "reputation": {}, "items": [{"id": "legendary_speedboat", "name": "Легендарный спидбот", "count": 1}]}, "branches": [{"condition": "Победа в гонке", "outcome": "Полное вознаграждение и легендарный спидбот", "next_quests": []}, {"condition": "Финиш без победы", "outcome": "Уменьшенное вознаграждение, нет спидбота", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока в ночной Biscayne Bay, где подпольные гонки на спидботах сочетают неон, музыку и риск полицейских\\nрейдов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Оплатить вступительный взнос и получить доступ к гаражу тюнинга.\\n2. Настроить speedboat: двигатель, стабилизаторы, навигацию и антирадар.\\n3. Пройти десять кругов по Biscayne Bay, маневрируя между соперниками и волнорезами.\\n4. Удерживать максимальную скорость на поворотах и отражать агрессивные приёмы противников.\\n5. Финишировать у South Beach и решить, принимать ли участие в последующих подпольных сериях.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Biscayne Bay окружён небоскрёбами даунтауна и ночными клубами South Beach.\\n- Cigarette boats исторически связаны с контрабандой и сериалом «Miami Vice».\\n- Подпольные гонки формируют репутацию в криминальных и элитных яхтенных клубах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Победа: 50 000 еддис и легендарный спидбот.\\n- Участие: 2 000 XP.\\n- Дополнительные бонусы за трюки и чистые круги.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Гонки на воде с физикой волн и влиянием погодных условий.\\n- Тюнинг транспорта и управление рисками поломки.\\n- Опциональный мультиплеер и рейтинги подпольных клубов.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест ведёт к линиям контрабанды, криминальных кланов и элитных клубов владельцев лодок Майами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1110, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Гонки на спидботах» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

