INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-french-concession', 'Шанхай 2020-2029 — Французская концессия', 'Игрок гуляет по Tianzifang и Xintiandi, пьёт кофе под платанами и завершает ужином в французском ресторане.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-french-concession", "title": "Шанхай 2020-2029 — Французская концессия", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:40:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["shanghai", "french_concession", "colonial-heritage"], "topics": ["urban-culture", "cafe-life"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "social-service", "culture-service"], "related_documents": [{"id": "github-issue-384", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/384", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/shanghai/2020-2029/quest-005-french-concession.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание французской концессии было в Markdown и не добавляло культурные баффы и атмосферу европейского квартала.", "goal": "Структурировать прогулку по French Concession в YAML, подчеркнуть платаны, шопинг и кафе.", "essence": "Игрок гуляет по Tianzifang и Xintiandi, пьёт кофе под платанами и завершает ужином в французском ресторане.", "key_points": ["Квест-id SHANGHAI-2029-005, сложность easy, длительность 1–2 часа.", "Маршрут сочетает арт-квартал Tianzifang, шопинг, кафе и исторические дома shikumen.", "Награда выдаёт 800 XP, +10 к культурному ресурсу и титул «Парижанин Шанхая»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Французская концессия — квартал платанов и арт-двориков; узкие longtang, shikumen и европейские кафе формируют атмосферу «Парижа Востока».\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Исследовать Tianzifang: арт-мастерские, галереи и сувениры.\\n2. Посетить кафе под платанами, попробовать местный кофе с французскими десертами.\\n3. Прогуляться по Xintiandi, изучая реконструированные shikumen.\\n4. Завершить ужином в французском ресторане, обсуждая связь культур.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны платаны вдоль улиц, архитектура art deco, shikumen, история концессии (1849–1943) и роль района в культуре Шанхая.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 100 едди на шопинг и ужин, получает 800 XP, +10 к культурному ресурсу и титул «Парижанин Шанхая», открывая дополнительные колониальные маршруты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890430, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Французская концессия» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

