cept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["miami", "art-basel", "networking"], "topics": ["culture", "elite"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "liveops-service"], "related_documents": [{"id": "quest-miami-2029-art-deco", "relation": "complements"}, {"id": "github-issue-1111", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1111", "relation": "migrated_to", "migrated_at": "2025-11-23T12:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/miami/2020-2029/quest-009-art-basel.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Арт-линия Майами требовала проработанного квеста про элитную ярмарку Art Basel и социальные связи.", "goal": "Дать игроку опыт посещения галерей, сделок и вечеринок.", "essence": "Игрок покупает билет на Art Basel, изучает экспозиции, общается с коллекционерами и решает, приобретать ли арт.", "key_points": ["Ярмарка объединяет мировых художников и NFT-инсталляции.", "VIP-зоны и яхтенные вечеринки раскрывают социальную элиту Майами.", "Квест приносит культурный бонус и контакты с коллекционерами."]}, "quest_definition": {"quest_type": "side", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "buy_art_basel_ticket", "text": "Приобрести билет на Art Basel", "type": "interact", "target": "art_basel_ticket_office", "count": 1, "optional": false}, {"id": "explore_galleries", "text": "Изучить экспозиции и NFT-инсталляции", "type": "interact", "target": "art_basel_galleries", "count": 1, "optional": false}, {"id": "meet_collectors", "text": "Общаться с коллекционерами и художниками", "type": "interact", "target": "art_basel_vip_zone", "count": 1, "optional": false}, {"id": "decide_on_art_purchase", "text": "Принять решение о покупке произведения искусства", "type": "choice", "target": "art_purchase_decision", "count": 1, "optional": false}, {"id": "attend_yacht_party", "text": "Посетить яхтенную вечеринку VIP-зоны", "type": "interact", "target": "art_basel_yacht_party", "count": 1, "optional": false}], "rewards": {"xp": 1500, "currency": -500, "attributes": {"cultural": 20, "social": 15}, "achievements": [{"id": "art_basel_vip", "name": "Art Basel VIP"}], "reputation": {}, "items": []}, "branches": [{"condition": "Покупка произведения искусства", "outcome": "Дополнительная репутация с коллекционерами, уникальный предмет", "next_quests": []}, {"condition": "Отказ от покупки", "outcome": "Стандартное вознаграждение", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Art Basel Miami Beach проходит в декабре. Игрок прибывает на ярмарку, проходит павильоны, слушает лекции и принимает\\nрешение о покупке произведений искусства, включая NFT и цифровые инсталляции.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Приобрести билет (обычный или VIP) и получить доступ в павильоны.\\n2. Исследовать галереи: живопись, скульптура, инсталляции, NFT-арт, VR-проекты.\\n3. Наладить networking с коллекционерами и галеристами.\\n4. Решить, покупать ли арт-объект: вложение, перепродажа или отказ.\\n5. Посетить after-party на яхте и закрепить связи.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP.\\n- Финансы: стоимость билетов и покупок (от -100 до -10 000 000 еддис).\\n- Культура: +20 и социальные контакты элиты.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Белые шатры на пляже, миллионеры в шортах, шампанское на каждом шагу, вечеринки у причала.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Социальное взаимодействие, экономические сделки, сбор культурного опыта.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с элитной линией Майами и геймплейными ветками art-deco.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1111, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Art Basel» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

