INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-akihabara-otaku', 'Токио 2020-2029 — Отаку Акихабары', 'Игрок исследует Акихабару, посещает мейд-кафе, активирует гача-автомат и формирует связи с локальным сообществом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-akihabara-otaku", "title": "Токио 2020-2029 — Отаку Акихабары", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:50:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "akihabara", "otaku"], "topics": ["subculture", "collection-loops"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "education-service", "market-system"], "related_documents": [{"id": "github-issue-386", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/386", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2020-2029/quest-003-akihabara-otaku.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-прохождение по Акихабаре не раскрывал механику гачи и социальные бонусы от отаку-сообщества.", "goal": "Структурировать квест в YAML, подчеркнуть коллекционный геймплей и социальное погружение в субкультуру.", "essence": "Игрок исследует Акихабару, посещает мейд-кафе, активирует гача-автомат и формирует связи с локальным сообществом.", "key_points": ["Квест-id TOKYO-2029-003, тип social, сложность easy, длительность 1–2 часа.", "Акцент на ролевом взаимодействии в мейд-кафе, коллекционировании фигурок и знакомстве с отаку-сетями.", "Награды включают 600 XP, социальную репутацию и случайный лут с редкостями."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Акихабара — квартал электроники и кибер-отаку культуры, где витрины хранят ностальгию, а AR-вывески уводят в альтернативные аниме-реальности.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Прогуляться по торговым улицам, исследуя магазины электроники и аниме-магазины.\\n2. Посетить мейд-кафе, выполнить сюжетный ролевой сценарий и получить тематический напиток.\\n3. Активировать гача-автомат, определить редкость фигурки и зафиксировать трофей в коллекции.\\n4. Приобрести лимитированную мангу и пообщаться с сообществом отаку.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "В районе звучит 8-битная музыка аркад, AR-анимешные персонажи приветствуют гостей, на улицах встречаются косплееры и любители ретро-комплектующих.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 600 XP, тратит до 2 000 едди, извлекает случайную фигурку (Common-Legendary) и +5 к репутации отаку-сообщества, открывая тематические ивенты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 261, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Отаку Акихабары» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

