-- Quest: canon-quest-shanghai-tower
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-tower', 'Шанхай 2020-2029 — Башня Шанхая', 'Игрок с помощью социальной инженерии попадает в башню, поднимается на 118-й этаж, затем добирается до шпиля и устанавливает хакерский модуль.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-tower", "title": "Шанхай 2020-2029 — Башня Шанхая", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:35:00+00:00", "github_issue_number": 383, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["shanghai", "skyscraper", "infiltration"], "topics": ["vertical-exploration", "high-tech"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "exploration-service", "hacking-service", "infiltration-service", "tourism-service"], "related_documents": [{"id": "github-issue-383", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/383", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/shanghai/2020-2029/quest-002-shanghai-tower.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Миссия в Shanghai Tower хранилась в Markdown и не подключала вертикальные и хакерские механики.", "goal": "Описать проникновение на 632‑метровую башню в YAML, включив лифты, технические этажи и установку узла на шпиле.", "essence": "Игрок с помощью социальной инженерии попадает в башню, поднимается на 118-й этаж, затем добирается до шпиля и устанавливает хакерский модуль.", "key_points": ["Квест-id SHANGHAI-2029-002, сложность hard, длительность 2–4 часа.", "Этапы включают вход в Lujiazui, подъём на 561 м, доступ на технические уровни и работу на 632‑метровом шпиле.", "Награда даёт 4 000 XP, 30 000 едди и титул «На вершине мира»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Shanghai Tower — второе по высоте здание планеты, спиральный небоскрёб с супербыстрыми лифтами и панорамой Пудуна.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Войти в башню через бизнес-лобби, используя социальную инженерии.\\n2. Подняться на смотровую площадку на 118-м этаже (561 м) и изучить охранные паттерны.\\n3. Получить доступ к техническим этажам, обойти датчики высоты.\\n4. Добраться до шпиля и установить хакерский узел, затем аккуратно эвакуироваться.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны спиральная форма башни, «три башни» Пудуна (Jin Mao, SWFC, Shanghai Tower), возможность видеть облака на высоте 500 м и панораму города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 4 000 XP, 30 000 едди, титул «На вершине мира» и улучшенный доступ к сетям Пудуна; при провале возможен смертельно опасный спад.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 383, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Башня Шанхая» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

