INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-yu-garden', 'Шанхай 2020-2029 — Сад Юй Юань', 'Игрок входит через Dragon Wall, проходит зигзагообразные мосты, посещает чайный павильон и завершает медитацией у пруда с кои.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-yu-garden", "title": "Шанхай 2020-2029 — Сад Юй Юань", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:40:00+00:00", "github_issue_number": 383, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["shanghai", "yu_garden", "mindfulness"], "topics": ["classical-chinese-garden", "meditation"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "culture-service", "wellbeing-service", "education-service"], "related_documents": [{"id": "github-issue-383", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/383", "relation": "migrated_to", "migrated_at": "2025-11-22T22:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/shanghai/2020-2029/quest-003-yu-garden.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Медитация в саду Юй Юань была записана в Markdown и не выдавалась в систему спокойствия и культурных маршрутов.", "goal": "Описать визит в классический сад династии Мин в YAML, акцентировать чайную церемонию и медитацию.", "essence": "Игрок входит через Dragon Wall, проходит зигзагообразные мосты, посещает чайный павильон и завершает медитацией у пруда с кои.", "key_points": ["Квест-id SHANGHAI-2029-003, сложность easy, длительность 1–2 часа.", "Маршрут включает Dragon Wall, пруд с кои, чайную церемонию и медитацию.", "Награда даёт 1 000 XP и бафф «Покой ума» (+10% Focus на 24 часа)."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Yu Garden — сад радости эпохи Мин (1559); его павильоны, пруды и rockery создают тишину посреди мегаполиса.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Войти через Dragon Wall и исследовать узкие тропинки.\\n2. Посетить пруд с карпами кои, сделать записи об архитектуре.\\n3. Принять участие в чайной церемонии, изучить ритуал заваривания.\\n4. Выполнить медитацию у павильона и зафиксировать эффекты покоя.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны извилистые мосты (духи ходят прямо), резные крыши с драконами, rockery, ароматы чая и тишина сада.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 20 едди, получает 1 000 XP, бафф «Покой ума» (+10% Focus на 24 часа) и титул «Гармония сада».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 383, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Сад Юй Юань» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

