INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-katana-smith', 'Токио 2020-2029 — Мастер Катаны', 'Игрок ищет кузнеца в горах, проходит проверку, настраивает дизайн катаны и ждёт изготовления, прежде чем получить легендарное оружие.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-katana-smith", "title": "Токио 2020-2029 — Мастер Катаны", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "1.2.0", "last_updated": "2025-12-12T12:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-12T12:00:00+00:00", "processed_by": "content-writer", "processed_at": "2025-12-12T12:00:00+00:00", "github_issue_number": 388, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "katana", "craftsmanship"], "topics": ["legendary-gear", "ceremonial-trials"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "crafting-service", "reputation-service", "culture-service", "progression-service"], "related_documents": [{"id": "github-issue-388", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/388", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2020-2029/quest-009-katana-smith.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История о заказе легендарной катаны не раскрывала механику испытаний, ожидания и уникальных бонусов оружия.", "goal": "Структурировать квест в YAML, описав дуэльное доказательство достоинства, цикл производства и церемонию вручения.", "essence": "Игрок ищет кузнеца в горах, проходит проверку, настраивает дизайн катаны и ждёт изготовления, прежде чем получить легендарное оружие.", "key_points": ["Квест-id TOKYO-2029-009, тип side, сложность hard, длительность 2–4 часа.", "Сюжет сочетает дуэль на боккэнах, инвестиции 50 000 едди и 30-дневное ожидание или ускорение через редкие ресурсы.", "Награда — легендарная катана с уникальным хамоном и +20% урона, открывающая самурайскую ветку."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Последний мастер катан использует тамахаганэ и кибернетические печи, сохраняя душу ремесла и обеспечивая заказчиков персонализированными клинками.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Найти кузницу в горах за пределами Токио, расшифровав подсказки коллекционеров оружия.\\n2. Пройти испытание на честь: дуэль на деревянных мечах и беседа о кодексе бусидо.\\n3. Выбрать параметры клинка, оплатить 50 000 едди и решить, ускорять ли производство редкими материалами.\\n4. Дождаться завершения ковки (30 игровых дней или ускорение) и вернуться на церемонию вручения.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 2 000 XP, легендарную катану с +20% к урону и кастомным внешним видом, а также рост репутации среди самурайских кланов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 388, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Мастер Катаны» в YAML и завершение цикла Concept Director."}, {"version": "1.2.0", "date": "2025-12-12", "author": "content_writer", "changes": "Финализация контента, подготовка к импорту в базу данных."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

