INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-chinese-opera', 'Шанхай 2020-2029 — Китайская опера', 'Игрок покупает билет, наблюдает первый акт с масками, пьёт чай в антракте, смотрит батальные сцены и завершает аплодисментами.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-chinese-opera", "title": "Шанхай 2020-2029 — Китайская опера", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:40:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["shanghai", "opera", "culture"], "topics": ["traditional-art", "performance"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "culture-service", "social-service", "music-system"], "related_documents": [{"id": "github-issue-385", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/385", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/shanghai/2020-2029/quest-008-chinese-opera.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Визит на китайскую оперу был в Markdown и не подключал культурные баффы и образовательные ветки.", "goal": "Перенести спектакль с масками и акробатикой в YAML, детализировать этапы, награды и эффекты.", "essence": "Игрок покупает билет, наблюдает первый акт с масками, пьёт чай в антракте, смотрит батальные сцены и завершает аплодисментами.", "key_points": ["Квест-id SHANGHAI-2029-008, сложность medium, длительность 2–4 часа.", "Описаны маски, символика цветов, акробатика, инструменты erhu и pipa.", "Награда 1 500 XP, -50 едди, +15 к культурному ресурсу и титул «Знаток оперы»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Китайская опера — синтез вокала, акробатики и масок; сцена наполнена gong, erhu и символикой цветов персонажей.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Купить билет в исторический театр и изучить афишу.\\n2. Первый акт: наблюдать маски (красный, чёрный, белый), высокие вокальные партии и акробатические номера.\\n3. Антракт: дегустировать чай, разговаривать с труппой о традициях.\\n4. Второй акт: батальные сцены с оружием, синхронные прыжки и финальные gong.\\n5. Финал: аплодисменты, получение сувенирной маски и запись впечатлений.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны расшитые костюмы, символика масок, инструменты erhu и pipa, гром gong и использование боевой хореографии.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок тратит 50 едди, получает 1 500 XP, +15 к культурному ресурсу и титул «Знаток оперы», открывая культурные цепочки восточного театра.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 140890430, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Китайская опера» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

