-- Quest: canon-quest-tokyo-yakuza-initiation
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-yakuza-initiation', 'Токио 2020-2029 — Инициация в Якудза', 'Игрок доказывает преданность синдикату, выполняет взыскание долга, проходит церемонию yubitsume и получает кибер-татуировку клана.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-yakuza-initiation", "title": "Токио 2020-2029 — Инициация в Якудза", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T11:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T11:50:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "yakuza", "faction"], "topics": ["criminal-underworld", "loyalty-tests"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "faction-service", "reputation-service", "security-service", "social-service"], "related_documents": [{"id": "github-issue-386", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/386", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}, {"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T11:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2020-2029/quest-002-yakuza-initiation.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-версия ритуала вступления в якудза не фиксировала последствия для фракционных связей и репутации игрока.", "goal": "Формализовать квест в YAML, описать выборы и риски, связанные с татуировками, долгами и присягой клана.", "essence": "Игрок доказывает преданность синдикату, выполняет взыскание долга, проходит церемонию yubitsume и получает кибер-татуировку клана.", "key_points": ["Квест-id TOKYO-2029-002, тип faction, сложность hard, длительность 2–4 часа.", "Испытания включают дипломатическое или силовое взыскание долга, церемонию в традиционном офисе и необратимое членство в клане.", "Награды открывают экосистему контрактов якудза, торговые скидки и защиту фракции."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Клан якудза модернизировал вековые традиции под киберпанк-реалии: цифровые татами-голограммы, биометрический контроль гостей и смешение ритуалов с нейросетевыми клятвами.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Познакомиться с вакагасира в ночном кабаке и получить приглашение на испытание.\\n2. Выбрать подход к должнику: переговоры с упором на долг, запугивание или быстрый рейд.\\n3. Вернуться в офис клана, пройти церемонию yubitsume или предложить равноценную кибер-жертву.\\n4. Пережить процесс нанесения полноформатной кибер-татуировки и вступительной клятвы перед кумитё.\\n5. Получить клановые протоколы и доступ к закрытой сети заказов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Интерьер сочетает татами, токонома с семейными реликвиями и скрытую технику: стены проецируют хронику клана, охрана использует смарт-кимоно и биометрические сенсоры.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 2 500 XP, 5 000 едди и уникальную татуировку, которая активирует ветку из 20+ квестов. Репутация с якудза растёт, полиция и конкуренты становятся враждебнее, выход из фракции недоступен.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 261, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Инициация в Якудза» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

