INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-emperor-audience', 'Токио 2030-2039 — Аудиенция у Императора', 'Игрок заслуживает аудиенцию в Императорском дворце и выбирает курс страны между традицией, модернизацией и революцией.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-emperor-audience", "title": "Токио 2030-2039 — Аудиенция у Императора", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:00:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "politics", "imperial-court"], "topics": ["diplomacy", "branching-outcomes"], "related_systems": ["narrative-service", "reputation-system", "world-state-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2030-2039/quest-020-emperor-audience.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не раскрывал политические последствия аудиенции и жёсткий протокол встречи с императором.", "goal": "Структурировать квест в YAML, описав требования к репутации, церемониал и ветвление будущего Японии.", "essence": "Игрок заслуживает аудиенцию в Императорском дворце и выбирает курс страны между традицией, модернизацией и революцией.", "key_points": ["Квест-id TOKYO-2039-020, тип main, сложность hard, длительность 4+ часа.", "Требует репутации Токио 50+, строгого соблюдения кэйго и прохождения процедур дворца.", "Финальные выборы меняют политический ландшафт Японии и открывают новые сюжетные ветви."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Императорский дворец окружён садами и охраной; аудиенция начинается с поклона в 90 градусов и строгих речевых форм.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Достигнуть требуемой репутации и получить официальное приглашение.\\n2. Пройти проверку безопасности и церемонию входа, надев формальное кимоно.\\n3. Провести аудиенцию, выбирая темы «Традиции», «Модернизация» или «Революция».\\n4. Распределить последствия выбора, влияющие на фракции, экономику и международные отношения.\\n", "mechanics_links": [], "assets": []}, {"id": "branching_outcomes", "title": "Варианты выбора", "body": "- Традиции: усилить роль императора и консерваторов, получить титул «Советник Императора».\\n- Модернизация: реформировать монархию в символическую роль, укрепить демократию и международные связи.\\n- Революция: инициировать кризис, отменить монархию и открыть ветку гражданского конфликта.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "5 000 XP, глобальные изменения политического состояния Японии, доступ к новым сюжетам и возможный титул «Советник Императора».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 262, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Аудиенция у Императора» в YAML и