INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-fuji-pilgrimage', 'Токио 2040-2060 — Паломничество на Фудзи', 'Игрок поднимается на Фудзи во время Time of the Red, преодолевает бурю, общается с монахами и встречает рассвет.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-fuji-pilgrimage", "title": "Токио 2040-2060 — Паломничество на Фудзи", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:05:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "mount-fuji", "pilgrimage"], "topics": ["spiritual-journeys", "endurance"], "related_systems": ["narrative-service", "pilgrimage-system", "mood-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2040-2060/quest-021-fuji-pilgrimage.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание восхождения на Фудзи не описывало влияние красных бурь и игровые баффы паломничества.", "goal": "Перевести квест в YAML, связав духовный опыт с механиками выносливости и коллекционирования печатей.", "essence": "Игрок поднимается на Фудзи во время Time of the Red, преодолевает бурю, общается с монахами и встречает рассвет.", "key_points": ["Квест-id TOKYO-2060-021, тип side, сложность medium, длительность 2–4 часа.", "Восхождение требует проверок выносливости, даёт паломнические печати и встречи с NPC.", "Награды дают бафф выносливости и духовный бонус."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Паломники поднимаются от пятой станции к вершине Фудзи, пробиваясь сквозь красный туман и соляные ветры эпохи Time of the Red.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Подготовиться на пятой станции, получить посох паломника и защиту от бурь.\\n2. Преодолеть участки красной бури, используя проверки выносливости и укрытия в хижинах.\\n3. Общаться с монахами, выполнять короткие медитативные задания и собирать печати станций.\\n4. Встретить рассвет на вершине, активировав сцену «горайко».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 2 000 XP, +20% к выносливости на 48 часов и +10 к духовности; достижение «Покоритель Фудзи» добавляется в профиль.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 263, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Паломничество на Фудзи» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches