INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-bay-pirates', 'Токио 2040-2060 — Пираты Токийского Залива', 'Игрок проходит посвящение у пиратов Одайбы, грабит суда и получает доступ к пиратской гильдии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-bay-pirates", "title": "Токио 2040-2060 — Пираты Токийского Залива", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:05:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "pirates", "water-combat"], "topics": ["faction-joining", "naval-battles"], "related_systems": ["narrative-service", "naval-combat-system", "faction-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2040-2060/quest-022-tokyo-bay-pirates.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о пиратах не описывал морские механики и развитие роли игрока в гильдии.", "goal": "Оформить квест в YAML, подчеркнув испытания, морские бои и экономику контроля залива.", "essence": "Игрок проходит посвящение у пиратов Одайбы, грабит суда и получает доступ к пиратской гильдии.", "key_points": ["Квест-id TOKYO-2060-022, тип faction, сложность hard, длительность 2–4 часа.", "Испытание включает ограбление транспорта, морской бой и распределение ролей на корабле.", "Награды дают 2 500 XP, 10 000 едди и доступ к пиратским контрактам."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "В затопленном доке Одайбы пиратская флотилия использует модифицированные катера и катаны для контроля поставок вокруг Токио.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Встретиться с капитаном и получить приглашение на испытание.\\n2. Провести рейд на торговое судно, используя водный бой и boarding-механику.\\n3. Принять роль на корабле (штурман, артиллерист, абордажник) и закрепить репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успешное вступление приносит 2 500 XP, 10 000 едди и доступ к пиратской гильдии, включая собственный апгрейдируемый катер.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 263, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Пираты Токийского Залива» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

