INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-philadelphia-2020-2029-independence-hall', 'Филадельфия 2020-2029 — «Индепенденс-Холл»', 'Игрок проходит по залам Independence Hall, участвует в реконструкциях и ощущает атмосферу подписания ключевых документов США.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enter_independence_hall", "text": "Войти в Independence Hall и начать экскурсию", "type": "interact", "target": "independence_hall_entry", "count": 1, "optional": false}, {"id": "visit_assembly_room", "text": "Посетить Assembly Room, где подписали Декларацию независимости и Конституцию", "type": "interact", "target": "assembly_room", "count": 1, "optional": false}, {"id": "watch_congress_reenactment", "text": "Наблюдать реконструкции заседаний Конгресса с участниками в исторических костюмах", "type": "interact", "target": "congress_reenactment", "count": 1, "optional": false}, {"id": "explore_halls", "text": "Исследовать залы Independence Hall и изучить экспонаты", "type": "interact", "target": "independence_hall_tour", "count": 1, "optional": false}]'::jsonb, '{"xp": 1800, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "independence_hall_visitor", "name": "Посетитель Independence Hall"}], "skill_boosts": {"history": 25}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-philadelphia-2020-2029-independence-hall", "title": "Филадельфия 2020-2029 — «Индепенденс-Холл»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["philadelphia", "quest", "history", "unesco"], "topics": ["city_quests", "heritage_sites"], "related_systems": ["narrative-service", "exploration-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/philadelphia/2020-2029/quest-002-independence-hall.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание визита в Independence Hall требует детализации, чтобы подчеркнуть историческую роль здания и его экспонатов.", "goal": "Создать образовательный квест в центре американской государственности с интерактивными экскурсиями.", "essence": "Игрок проходит по залам Independence Hall, участвует в реконструкциях и ощущает атмосферу подписания ключевых документов США.", "key_points": ["Фокус на Assembly Room, где подписали Декларацию независимости и Конституцию.", "Реконструкции заседаний Конгресса и участники в исторических костюмах.", "Связь с маршрутом Liberty Bell и расширением исторической ветки города."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enter_independence_hall", "text": "Войти в Independence Hall и начать экскурсию", "type": "interact", "target": "independence_hall_entry", "count": 1, "optional": false}, {"id": "visit_assembly_room", "text": "Посетить Assembly Room, где подписали Декларацию независимости и Конституцию", "type": "interact", "target": "assembly_room", "count": 1, "optional": false}, {"id": "watch_congress_reenactment", "text": "Наблюдать реконструкции заседаний Конгресса с участниками в исторических костюмах", "type": "interact", "target": "congress_reenactment", "count": 1, "optional": false}, {"id": "explore_halls", "text": "Исследовать залы Independence Hall и изучить экспонаты", "type": "interact", "target": "independence_hall_tour", "count": 1, "optional": false}], "rewards": {"xp": 1800, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "independence_hall_visitor", "name": "Посетитель Independence Hall"}], "skill_boosts": {"history": 25}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает игрока посетить Independence Hall — место, где родилась американская государственность. Экскурсия\\nсочетает реальный тур, AR-вставки и образовательные мини-игры.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "tour", "title": "Экскурсионный маршрут", "body": "1. Получение бесплатного билета и прохождение контроля безопасности.\\n2. Посещение Assembly Room, где подписали Декларацию независимости в 1776 году.\\n3. Знакомство с залом Конституции и обсуждением 1787 года.\\n4. Осмотр оригинальной мебели и экспонатов, сохранённых с XVIII века.\\n", "mechanics_links": ["mechanics/exploration/route-planning.yaml"], "assets": []}, {"id": "reenactment", "title": "Реконструкция", "body": "- Актёры в образах отцов-основателей проводят симуляцию дебатов.\\n- Игрок отвечает на вопросы викторины, выбирая аргументы разных делегаций.\\n- Возможность получить копию Декларации с персонализированным посланием.\\n", "mechanics_links": ["mechanics/social/social-dialogue.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP: 3 000, Валюта: 0.\\n- Исторический показатель +30 и ачивка «Отец-основатель».\\n- Доступ к закрытым лекциям и музеям в пределах Independence National Historical Park.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Гибрид музейного тура и интерактивной реконструкции.\\n- Система вопросов и ответов с оценкой знаний игрока.\\n- Коллекционирование записей выступлений и копий исторических документов.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест продолжает историческую ветку Филадельфии, связанную с Liberty Bell и Музеем американской революции, что усиливает\\nчувство единого маршрута по старым кварталам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1084, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Индепенденс-Холл» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

