-- Quest: canon-quest-tokyo-shrine-offering
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-shrine-offering', 'Токио 2020-2029 — Приношение в Храме', 'Игрок проходит через тории Мэйдзи Дзингу, выполняет омовение, приносит монету и тянет омикудзи, получая эффекты удачи.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-shrine-offering", "title": "Токио 2020-2029 — Приношение в Храме", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "1.2.0", "last_updated": "2025-12-12T12:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-12T12:00:00+00:00", "processed_by": "content-writer", "processed_at": "2025-12-12T12:00:00+00:00", "github_issue_number": 388, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "shrine", "spirituality"], "topics": ["ritual-games", "luck-systems"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "wellbeing-service"], "related_documents": [{"id": "github-issue-388", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/388", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2020-2029/quest-008-shrine-offering.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий посещения синтоистского храма был описан в Markdown без привязки к системе удачи и культурным баффам.", "goal": "Формализовать храмовый ритуал в YAML, показав механики очищения, приношений и случайных предсказаний.", "essence": "Игрок проходит через тории Мэйдзи Дзингу, выполняет омовение, приносит монету и тянет омикудзи, получая эффекты удачи.", "key_points": ["Квест-id TOKYO-2029-008, тип social, сложность easy, длительность 30–60 минут.", "Ритуал включает очищение, хлопки, поклон и взаимодействие с омикудзи, влияющими на удачу.", "Баффы удачи и достижение «Благословение Ками» стимулируют духовные сюжетные ветки."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Мэйдзи Дзингу остаётся оазисом спокойствия в центре неонового Токио, где игроки ищут благословение ками перед важными миссиями.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Пройти через тории, минуя толпы туристов и добираясь до внутреннего двора.\\n2. Выполнить омовение рук и рта у темидзуя, следуя подсказкам NPC-монаха.\\n3. Бросить монету в ящик приношений, сделать два хлопка и глубокий поклон.\\n4. Вытянуть омикудзи и получить случайное предсказание, закрепив его на древе удачи или забрав с собой.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Лесной коридор, аромат благовоний, деревянные эма с желаниями игроков и монахи в белых хакама создают аутентичную атмосферу.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок тратит 5 йен, получает 400 XP и шанс на бафф +5% к удаче на 24 часа. Неблагоприятное омикудзи можно нейтрализовать повторным посещением.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 388, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Приношение в Храме» в YAML и завершение цикла Concept Director."}, {"version": "1.2.0", "date": "2025-12-12", "author": "content_writer", "changes": "Финализация контента, подготовка к импорту в базу данных."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

