INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-anime-production', 'Токио 2040-2060 — Производство Аниме', 'Игрок руководит студией, создаёт 12 серий аниме и борется со стрессом и ожиданиями аудитории.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-anime-production", "title": "Токио 2040-2060 — Производство Аниме", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:05:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "anime", "creative-industry"], "topics": ["production", "burnout"], "related_systems": ["narrative-service", "career-system", "economy-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2040-2060/quest-024-anime-production.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown о создании аниме не отражал менеджмент команды, дедлайны и награды производства.", "goal": "Формализовать квест в YAML, описав циклы производства, выбор ролей и реакцию фанатов.", "essence": "Игрок руководит студией, создаёт 12 серий аниме и борется со стрессом и ожиданиями аудитории.", "key_points": ["Квест-id TOKYO-2060-024, тип social, сложность medium, длительность 4+ часов.", {"Выбор роли влияет на мини-игры": "раскадровка, анимация, режиссура."}, "Успех приносит крупный доход и славу, провал даёт опыт и шанс на ребут."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Аниме-студия в Синдзюку заполнена планшетами, перегретыми ПК и сеiyuu, записывающими реплики в ночные смены.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Выбрать роль в команде и подготовить концепт сезона.\\n2. Организовать производство, балансируя дедлайны, бюджет и настроение сотрудников.\\n3. Провести пост-продакшн, нанять сеiyuu и подготовить премьеру.\\n4. Отследить реакцию зрителей и решить, продлевать ли сериал.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успех даёт 4 000 XP, пассивный доход до 100 000 едди и +15 к славе; провал — 2 000 XP и возможность переобучения команды.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 263, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Производство Аниме» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

