INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-onsen-relaxation', 'Токио 2040-2060 — Расслабление в Онсэне', 'Игрок отправляется в Хаконэ, проходит ритуалы очищения и получает длительный бонус к восстановлению и снижению стресса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-onsen-relaxation", "title": "Токио 2040-2060 — Расслабление в Онсэне", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:05:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "onsen", "wellness"], "topics": ["recovery", "social-relax"], "related_systems": ["narrative-service", "rest-system", "mood-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2040-2060/quest-026-onsen-relaxation.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание онсэна не связывало расслабление с системами восстановления и социальной динамикой.", "goal": "Структурировать визит в онсэн в YAML, зафиксировать баффы, этапы процедуры и культурные акценты.", "essence": "Игрок отправляется в Хаконэ, проходит ритуалы очищения и получает длительный бонус к восстановлению и снижению стресса.", "key_points": ["Квест-id TOKYO-2060-026, тип social, сложность easy, длительность 1–2 часа.", "Онсэн даёт полный реген, -100% стресса на 24 часа и культурную социализацию.", "Затраты составляют 3 000 едди, доступен повторный запуск."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Горячие источники в Хаконэ сочетают ритуалы очищения, виды на Фудзи и камерный релакс; игрок знакомится с NPC и принимает участие в традиционном кайсэки-ужине.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Добраться до онсэна, соблюдая правила тишины и чистоты.\\n2. Пройти ритуал омовения, подготовив тело к погружению.\\n3. Погрузиться в ротэн-буро, активировав баффы восстановления и снятия стресса.\\n4. Провести социальное взаимодействие с гостями и насладиться традиционным обедом.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок расходует 3 000 едди, получает 600 XP, полное восстановление, -100% стресса на 24 часа и достижение «Мастер Расслабления».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 263, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Расслабление в Онсэне» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDE