INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-philadelphia-2020-2029-ben-franklin', 'Филадельфия 2020-2029 — «Бенджамин Франклин»', 'Игрок исследует дом, музей и могилу Франклина, собирая изобретения и поддерживая традиции Филадельфии.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_franklin_court", "text": "Посетить Franklin Court", "type": "interact", "target": "franklin_court_location", "count": 1, "optional": false}, {"id": "explore_museum", "text": "Исследовать музей изобретений Франклина", "type": "interact", "target": "franklin_inventions_museum", "count": 1, "optional": false}, {"id": "visit_print_shop", "text": "Посетить типографию", "type": "interact", "target": "franklin_print_shop", "count": 1, "optional": false}, {"id": "visit_grave", "text": "Посетить могилу Франклина и совершить традиционное подношение монет", "type": "interact", "target": "franklin_grave", "count": 1, "optional": false}]'::jsonb, '{"xp": 1800, "currency": 0, "items": [], "reputation": {"scientific": 45}, "achievements": [], "skill_boosts": {}}'::jsonb, '[{"condition": "Успешно собраны все изобретения", "outcome": "Открытие новых технологических квестов", "next_quests": []}]'::jsonb, '{"metadata": {"id": "canon-quest-philadelphia-2020-2029-ben-franklin", "title": "Филадельфия 2020-2029 — «Бенджамин Франклин»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-23T02:55:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["philadelphia", "quest", "franklin"], "topics": ["science-heritage", "enlightenment"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-1088", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1088", "relation": "migrated_to", "migrated_at": "2025-11-23T02:55:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/philadelphia/2020-2029/quest-006-ben-franklin.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание наследия Бенджамина Франклина существовало в Markdown и не связывалось с научной линией города.", "goal": "Оформить посещение Franklin Court и музея изобретений в формате YAML с упором на образовательный опыт.", "essence": "Игрок исследует дом, музей и могилу Франклина, собирая изобретения и поддерживая традиции Филадельфии.", "key_points": ["Маршрут охватывает Franklin Court, музей изобретений и типографию.", "Финал квеста требует посетить могилу и совершить традиционное подношение монет.", "Награда усиливает научную репутацию и открывает новые технологические квесты."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_franklin_court", "text": "Посетить Franklin Court", "type": "interact", "target": "franklin_court_location", "count": 1, "optional": false}, {"id": "explore_museum", "text": "Исследовать музей изобретений Франклина", "type": "interact", "target": "franklin_inventions_museum", "count": 1, "optional": false}, {"id": "visit_print_shop", "text": "Посетить типографию", "type": "interact", "target": "franklin_print_shop", "count": 1, "optional": false}, {"id": "visit_grave", "text": "Посетить могилу Франклина и совершить традиционное подношение монет", "type": "interact", "target": "franklin_grave", "count": 1, "optional": false}], "rewards": {"xp": 1800, "currency": 0, "items": [], "reputation": {"scientific": 45}, "achievements": [], "skill_boosts": {}}, "branches": [{"condition": "Успешно собраны все изобретения", "outcome": "Открытие новых технологических квестов", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с наследием Бенджамина Франклина — полимата, политического деятеля и изобретателя. Через\\nэкскурсию по Franklin Court и музею игрок учится ценить инновации и дух Просвещения.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "route", "title": "Маршрут", "body": "1. Посещение исторического квартала и голографической реконструкции дома Франклина.\\n2. Осмотр музея изобретений: бифокальные очки, громоотвод, печь Франклина, воздушный змей.\\n3. Демонстрация печатного станка и возможность издать собственный памфлет.\\n4. Завершение у могилы на Christ Church Burial Ground с традицией бросить монеты на удачу.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "knowledge", "title": "Интерактивные элементы", "body": "- Мини-игра с направлением электричества через «воздушный змей».\\n- Сбор коллекции «изобретения Франклина» как цифровых экспонатов.\\n- Диалоги с музейными гидами, раскрывающими влияние Франклина на науку и политику.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Музей подчёркивает роль Франклина в развитии Филадельфии: его лицо на купюре $100, вклад в электричество и в\\nсоциальные институты города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 500 XP, наука +20, расходы -10.\\n- Ачивка «Ученик Франклина» и доступ к дополнительным научным контрактам региона.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Образовательные панели с проверкой знаний о Франклине.\\n- Система коллекционирования изобретений и бонусов за их демонстрацию в других квестах.\\n- Социальная традиция подношения монет на могиле.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Поддерживает научную ветку Филадельфии и служит вводной точкой для цепочек про технологические инновации.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1088, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Бенджамин Франклин» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

