INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-003-cloud-gate', 'Чикаго — Cloud Gate (The Bean)', 'Игроки посещают зеркальную скульптуру, исследуют отражения и взаимодействуют с туристическим потоком Millennium Park.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_millennium_park", "text": "Прибыть в Millennium Park и активировать сцену «The Bean»", "type": "travel", "target": "millennium_park_cloud_gate", "count": 1, "optional": false}, {"id": "explore_reflections", "text": "Рассмотреть искажения skyline, пройти под скульптурой", "type": "interact", "target": "cloud_gate_sculpture", "count": 1, "optional": false}, {"id": "take_photo", "text": "Сделать фотографию (соц-механика), зарегистрировать эмоцию «Арт-селфи»", "type": "interact", "target": "photo_social_media", "count": 1, "optional": false}, {"id": "get_invitation", "text": "Получить приглашение на вечерний арт-маршрут парка", "type": "dialogue", "target": "park_guide_npc", "count": 1, "optional": true}]'::jsonb, '{"experience": 500, "money": 0, "reputation": {"aesthetic": 10}, "items": [], "unlocks": {"achievements": ["Боб-Селфи"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-003-cloud-gate", "title": "Чикаго — Cloud Gate (The Bean)", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "public-art", "tourism"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "social-service", "events-service"], "related_documents": [{"id": "questline-chicago-architecture", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/quest-003-cloud-gate.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест Cloud Gate описан без структуры и не подключён к архитектурной линейке Millennium Park.", "goal": "Стандартизировать опыт посещения «The Bean» в рамках городской туроперационной петли.", "essence": "Игроки посещают зеркальную скульптуру, исследуют отражения и взаимодействуют с туристическим потоком Millennium Park.", "key_points": ["Уникальный зеркальный объект Аниша Капура площадью 110 тонн.", "Акцент на фотокультуру и социальные сцены.", "Связь с архитектурным нарративом Чикаго."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_millennium_park", "text": "Прибыть в Millennium Park и активировать сцену «The Bean»", "type": "travel", "target": "millennium_park_cloud_gate", "count": 1, "optional": false}, {"id": "explore_reflections", "text": "Рассмотреть искажения skyline, пройти под скульптурой", "type": "interact", "target": "cloud_gate_sculpture", "count": 1, "optional": false}, {"id": "take_photo", "text": "Сделать фотографию (соц-механика), зарегистрировать эмоцию «Арт-селфи»", "type": "interact", "target": "photo_social_media", "count": 1, "optional": false}, {"id": "get_invitation", "text": "Получить приглашение на вечерний арт-маршрут парка", "type": "dialogue", "target": "park_guide_npc", "count": 1, "optional": true}], "rewards": {"experience": 500, "money": 0, "reputation": {"aesthetic": 10}, "items": [], "unlocks": {"achievements": ["Боб-Селфи"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Cloud Gate (The Bean) — зеркальная скульптура в Millennium Park, ставшая символом современной архитектуры Чикаго. Квест фиксирует посещение объекта, взаимодействие с отражениями и социальную активность туристов.\\n", "mechanics_links": ["mechanics/social/tourism-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Особенности объекта", "body": "- Официальное название Cloud Gate, но горожане называют «The Bean».\\n- 110 тонн отполированной нержавеющей стали.\\n- Автор — Аниш Капур, завершение проекта в 2004 году.\\n- Скульптура отражает небоскрёбы и жизнь Millennium Park.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/chicago-architecture.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы посещения", "body": "1. Прибыть в Millennium Park и активировать сцену «The Bean».\\n2. Рассмотреть искажения skyline, пройти под скульптурой.\\n3. Сделать фотографию (соц-механика), зарегистрировать эмоцию «Арт-селфи».\\n4. Получить приглашение на вечерний арт-маршрут парка.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 500 XP, эстетический показатель +10.\\n- Ачивка «Боб-Селфи».\\n- Открытие архитектурной линии Чикаго и фотозон города.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "immersion", "title": "Атмосфера и связи", "body": "Cloud Gate работает как социальный хаб: здесь музыканты, тату-артисты и гиды запускают события. Квест расширяет знание о Millennium Park и помогает накапливать туристические метрики.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1185, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-chicago-2029-003"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Формализован квест Cloud Gate для туристической ветки Чикаго."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

