-- Quest: canon-quest-tokyo-capsule-hotel
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-capsule-hotel', 'Токио 2020-2029 — Отель-Капсула', 'Игрок резервирует капсулу, соблюдает японский этикет, делит пространство с соседями и получает восстановление ресурсов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-capsule-hotel", "title": "Токио 2020-2029 — Отель-Капсула", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "1.2.0", "last_updated": "2025-12-12T12:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-12T12:00:00+00:00", "processed_by": "content-writer", "processed_at": "2025-12-12T12:00:00+00:00", "github_issue_number": 387, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "capsule-hotel", "slice-of-life"], "topics": ["micro-housing", "rest-loops"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "wellbeing-service", "exploration-service", "tourism-service"], "related_documents": [{"id": "github-issue-387", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/387", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2020-2029/quest-006-capsule-hotel.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание ночёвки в капсульном отеле не связывалось с системами отдыха и социального взаимодействия.", "goal": "Перевести опыт проживания в капсуле в структурированный YAML и подчеркнуть UX-эффекты для экономного жилья.", "essence": "Игрок резервирует капсулу, соблюдает японский этикет, делит пространство с соседями и получает восстановление ресурсов.", "key_points": ["Квест-id TOKYO-2029-006, тип side, сложность easy, длительность 30–60 минут.", "Геймплей строится на ритуалах заселения, ограниченном пространстве и социальном взаимодействии с постояльцами.", "Награды дают 300 XP, полный отдых и погружение в low-cost жильё Токио."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Капсульные отели Токио обслуживают салариманов и туристов, предлагая минималистичные капсулы с встроенными интерфейсами и общими зонами.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Забронировать капсулу, следуя правилам заведения и оплатив 500 едди.\\n2. Пройти японский ритуал заселения: снять обувь, воспользоваться шкафчиками и принять душ.\\n3. Устроиться в капсуле, активировать интерфейсы и настроить шумоподавление.\\n4. Провести время с соседями, обменявшись историями и бонусами к репутации.\\n5. Проснуться под звуки железной дороги и завершить отдых в общественной бане.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Отмечены узкие коридоры, тихие капсулы с индивидуальными панелями, запах кофе из общих автоматов и приглушённый гул поезда за окнами.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок тратит 500 едди, получает 300 XP и полностью восстанавливает здоровье и выносливость. Социальные реплики повышают доверие салариманов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 387, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Отель-Капсула» в YAML и завершение цикла Concept Director."}, {"version": "1.2.0", "date": "2025-12-12", "author": "content_writer", "changes": "Финализация контента, подготовка к импорту в базу данных."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

