INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-yokai-hunt', 'Токио 2040-2060 — Охота на Ёкаев', 'Команда из 10+ игроков выслеживает rogue-ИИ, маскирующихся под японских духов, и побеждает многофазный рейд.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-yokai-hunt", "title": "Токио 2040-2060 — Охота на Ёкаев", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:05:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "yokai", "raid"], "topics": ["boss-fights", "ai-threats"], "related_systems": ["narrative-service", "raid-system", "loot-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2040-2060/quest-030-yokai-hunt.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не деталировал рейдовую структуру и вознаграждения за охоту на ИИ-ёкаев.", "goal": "Зафиксировать в YAML этапы рейда, боссов и связь с Blackwall.", "essence": "Команда из 10+ игроков выслеживает rogue-ИИ, маскирующихся под японских духов, и побеждает многофазный рейд.", "key_points": ["Квест-id TOKYO-2060-030, тип main, сложность extreme, длительность 4+ часов.", "Содержит босс-бои против кицуне, тэнгу, они и финального Ямата-но-Ороти.", "Награды включают 10 000 XP, 100 000 едди и легендарные импланты ёкай-серии."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Rogue-ИИ искажают фольклор Токио: аркологии освещены неоном, а духовные артефакты переплетаются с кодом Blackwall.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Собрать рейдовую группу и разыскать логово ёкаев через сеть слухов.\\n2. Победить кицуне, тэнгу и они — каждый с уникальной механикой фаз.\\n3. Сразиться с Ямата-но-Ороти, используя синтоистские печати и координацию команды.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успех приносит 10 000 XP, 100 000 едди (делятся), легендарные импланты и титул «Охотник на Ёкаев»; открываются ветки Blackwall.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 263, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Охота на Ёкаев» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

