INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-sumo-tournament', 'Токио 2020-2029 — Турнир Сумо', 'Игрок вступает в турнир, сражается на дохё с экзокостюмом, соблюдает ритуалы очищения и претендует на чемпионство.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-sumo-tournament", "title": "Токио 2020-2029 — Турнир Сумо", "document_type": "canon", "category": "timeline-author", "status": "approved", "version": "1.2.0", "last_updated": "2025-12-12T12:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-12T12:00:00+00:00", "processed_by": "content-writer", "processed_at": "2025-12-12T12:00:00+00:00", "github_issue_number": 388, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "sumo", "cyber-sports"], "topics": ["arena-events", "cultural-fusion"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "combat-service", "liveops-service", "social-service", "culture-service"], "related_documents": [{"id": "github-issue-388", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/388", "relation": "migrated_to", "migrated_at": "2025-11-22T22:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2020-2029/quest-007-sumo-tournament.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-турнир сумо не фиксировал гибрид традиций и экзокостюмов и не описывал системные награды.", "goal": "Оформить кибер-сумо в YAML, подчеркнув стадионную атмосферу, прогрессию боёв и титул ёкодзуна.", "essence": "Игрок вступает в турнир, сражается на дохё с экзокостюмом, соблюдает ритуалы очищения и претендует на чемпионство.", "key_points": ["Квест-id TOKYO-2029-007, тип event, сложность medium, длительность 1–2 часа.", "Бои развивают гибрид традиционного сумо и технологий экзоскелетов, завершаясь схваткой с ёкодзуной.", "Победа приносит 30 000 едди и титул «Ёкодзуна», усиливая репутацию в кибер-спортклубах."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Турнир сумо в Токио соединяет вековые ритуалы с нейроусиленными экзокостюмами, привлекая зрителей и трансляции по всему мегаполису.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Пройти регистрацию и оплатить взнос в 2 000 едди, получив доступ к экзокостюму.\\n2. Выполнить ритуал очищения солью и войти на дохё под руководством гёдзи.\\n3. Провести серию матчей, используя толчки, броски и силовые импульсы костюма.\\n4. В финале победить ёкодзуну, адаптируясь к его усиленному оборудованию и контратакам.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны традиционные барабаны тайко, голографические флаги кланов, семпаи в кимоно и толпы фанатов, ставящих ставки в AR-интерфейсах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Чемпион получает 30 000 едди, титул «Ёкодзуна» и доступ к элитным турнирам. Поражение даёт опыт и репутацию в кибер-спортсообществе.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 388, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Турнир Сумо» в YAML и завершение цикла Concept Director."}, {"version": "1.2.0", "date": "2025-12-12", "author": "content_writer", "changes": "Финализация контента, подготовка к импорту в базу данных."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

