INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-ninja-clan', 'Токио 2030-2039 — Клан Ниндзя', 'Игрок разыскивает секретное додзё, проходит жесткий отбор и получает доступ к уникальным техникам ниндзя.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-ninja-clan", "title": "Токио 2030-2039 — Клан Ниндзя", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:00:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "ninja", "stealth-faction"], "topics": ["stealth", "advanced-combat"], "related_systems": ["narrative-service", "stealth-system", "faction-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2030-2039/quest-015-ninja-clan.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о клане ниндзя не описывал испытания, обучение и уникальные стелс-техники.", "goal": "Представить вступление в клан в YAML, раскрыть испытания и набор кибер-нинпо.", "essence": "Игрок разыскивает секретное додзё, проходит жесткий отбор и получает доступ к уникальным техникам ниндзя.", "key_points": ["Квест-id TOKYO-2039-015, тип faction, сложность extreme, длительность 4+ часа.", "Испытания требуют стелса DC 22, бесшумных устранений и овладения десятью техниками нинпо.", "Награды включают членство в клане, стелс-импланты и ассасинские контракты."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Современный клан ниндзя скрывается в подземных уровнях мегаполиса, используя кибер-импланты и AR-ритуалы для тренировок.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Собрать слухи о возвращении клана и отыскать их портал в подземный храм.\\n2. Пройти проверку чести и выживания: скрытное проникновение и безупречное уничтожение мишеней.\\n3. Изучить десять техник нинпо под руководством мастера и настроить кибер-импланты.\\n4. Принять клятву, надеть чёрную маску и получить доступ к контрактам клана.\\n", "mechanics_links": [], "assets": []}, {"id": "ninja_techniques", "title": "Техники и способности", "body": "Включены сюнсин-но-дзюцу (рывок), кагэ-буншин (голографические двойники), дзё-дзюцу (усиленный стелс) и другие комбинаторные умения.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 5 000 XP, доступ к ассасин-контрактам, уникальные стелс-импланты и повышение репутации среди теневых фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 262, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Клан Ниндзя» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level