-- Quest: quest-philadelphia-2029-brotherly-love
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-philadelphia-2029-brotherly-love', 'Филадельфия 2020-2029 — Город братской любви', 'Игрок исследует ключевые локации Penn''s Landing и City Hall, размышляя об идеалах и практиках города.', 'side', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_penns_landing", "text": "Исследовать ключевые локации Penn''s Landing", "type": "interact", "target": "penns_landing_location", "count": 1, "optional": false}, {"id": "visit_city_hall", "text": "Посетить City Hall и изучить историю Уильяма Пенна", "type": "interact", "target": "philadelphia_city_hall", "count": 1, "optional": false}, {"id": "reflect_ideals", "text": "Размышлять об идеалах братства и религиозной свободы", "type": "interact", "target": "quaker_ideals_reflection", "count": 1, "optional": false}, {"id": "make_moral_choice", "text": "Принять моральный выбор между идеалами и современной реальностью", "type": "choice", "target": "brotherly_love_moral_choice", "count": 1, "optional": false}]'::jsonb, '{"xp": 3000, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "brotherly_love_philosopher", "name": "Философ братской любви"}], "skill_boosts": {}}'::jsonb, '[{"condition": "Выбор в пользу идеалов", "outcome": "Высокая моральная репутация", "next_quests": []}, {"condition": "Выбор в пользу реальности", "outcome": "Прагматичная репутация", "next_quests": []}]'::jsonb, '{"metadata": {"id": "quest-philadelphia-2029-brotherly-love", "title": "Филадельфия 2020-2029 — Город братской любви", "document_type": "canon", "category": "timeline-quest", "status": "content-ready", "version": "1.1.0", "last_updated": "2025-11-23T12:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["philadelphia", "history", "philosophy"], "topics": ["civic_identity", "cultural_heritage"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1092", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1092", "relation": "migrated_to", "migrated_at": "2025-11-23T02:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/philadelphia/2020-2029/quest-010-brotherly-love.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Философская линия Филадельфии не раскрывала происхождение названия города и ценности квакеров.", "goal": "Показать идеалы братства и религиозной свободы, заложенные Уильямом Пенном, и противопоставить их современной реальности.", "essence": "Игрок исследует ключевые локации Penn''s Landing и City Hall, размышляя об идеалах и практиках города.", "key_points": ["Название Philadelphia происходит от греческих слов «любовь» и «брат».", "Уильям Пенн основал город в 1682 году как убежище толерантности для квакеров.", "Контраст между провозглашёнными идеалами и современной городской жизнью создаёт моральный выбор."]}, "quest_definition": {"quest_type": "side", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_penns_landing", "text": "Исследовать ключевые локации Penn''s Landing", "type": "interact", "target": "penns_landing_location", "count": 1, "optional": false}, {"id": "visit_city_hall", "text": "Посетить City Hall и изучить историю Уильяма Пенна", "type": "interact", "target": "philadelphia_city_hall", "count": 1, "optional": false}, {"id": "reflect_ideals", "text": "Размышлять об идеалах братства и религиозной свободы", "type": "interact", "target": "quaker_ideals_reflection", "count": 1, "optional": false}, {"id": "make_moral_choice", "text": "Принять моральный выбор между идеалами и современной реальностью", "type": "choice", "target": "brotherly_love_moral_choice", "count": 1, "optional": false}], "rewards": {"xp": 3000, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "brotherly_love_philosopher", "name": "Философ братской любви"}], "skill_boosts": {}}, "branches": [{"condition": "Выбор в пользу идеалов", "outcome": "Высокая моральная репутация", "next_quests": []}, {"condition": "Выбор в пользу реальности", "outcome": "Прагматичная репутация", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает игроку исследовать истоки прозвища «Город братской любви», посетить исторические места и собрать\\nсвидетельства того, как идеалы квакеров проявляются в сегодняшней Филадельфии.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Изучение корней названия\\nЛокация: Историческая библиотека Филадельфии, центр города\\nОписание: Игрок изучает греческие корни слова «Philadelphia» и план Уильяма Пенна для города.\\nЦели:\\n  - Изучить греческие корни слова «Philadelphia» (philos - любовь, adelphos - брат)\\n  - Ознакомиться с планом Уильяма Пенна для города\\n  - Узнать об идеалах квакеров и религиозной свободы\\n  - Проверка навыков: Investigation 0.3+ для глубокого изучения\\n\\nЭтап 2: Penn''s Landing\\nЛокация: Penn''s Landing, набережная реки Делавэр\\nОписание: Игрок посещает историческое место прибытия первых колонистов.\\nЦели:\\n  - Посетить Penn''s Landing и мемориальные объекты\\n  - Изучить свидетельства прибытия первых колонистов в 1682 году\\n  - Зафиксировать исторические детали о прибытии Уильяма Пенна\\n  - Проверка навыков: History 0.3+ для понимания исторического контекста\\n\\nЭтап 3: City Hall и статуя Пенна\\nЛокация: City Hall, статуя Уильяма Пенна на вершине\\nОписание: Игрок поднимается к статуе Пенна на City Hall, изучая обзорные точки и общаясь с горожанами.\\nЦели:\\n  - Подняться к статуе Пенна на City Hall\\n  - Зафиксировать обзорные точки города\\n  - Поговорить с горожанами об идеалах города\\n  - Проверка навыков: Perception 0.4+ для наблюдения за городом\\n\\nЭтап 4: Сопоставление идеалов и реальности\\nЛокация: Различные районы Филадельфии\\nОписание: Игрок сопоставляет идеалы религиозной свободы с современными социальными вызовами.\\nЦели:\\n  - Изучить современные социальные вызовы города\\n  - Сопоставить идеалы религиозной свободы с реальностью\\n  - Обсудить с местными жителями современное состояние города\\n  - Проверка навыков: Philosophy 0.3+ для глубокого анализа\\n\\nЭтап 5: Моральный выбор\\nЛокация: Филадельфийский философский клуб\\nОписание: Игрок делает выбор: поддержать оптимистичную трактовку или подчеркнуть разрыв между идеалом и реальностью.\\nЦели:\\n  - Принять участие в философской дискуссии\\n  - Выбрать позицию: оптимистичная трактовка или реалистичная оценка\\n  - Задокументировать свой выбор и обоснование\\n  - Проверка навыков: Persuasion 0.3+ для аргументации своей позиции\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Филадельфия основана в 1682 году как центр толерантности. Наследие квакеров до сих пор отражается в архитектуре\\nи городских праздниках. Penn''s Landing служит символическим порталом в историю города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Базовые награды:\\n  - Опыт: 1 500 XP\\n  - Атрибут Философия: +20\\n  - Репутация Philadelphia Community: +25\\n  - Репутация Philadelphia Historians: +15\\n  - Репутация Philadelphia Philosophers: +10\\n\\nУникальные награды:\\n  - Достижение \\"Брат филадельфиец\\" (базовое)\\n  - Предмет \\"План Уильяма Пенна\\" (декоративный)\\n  - Открытие доступа к квесту \\"Филадельфия: история американской демократии\\"\\n\\nДополнительные награды (при успешных проверках навыков):\\n  - +5 XP за успешную проверку Investigation\\n  - +5 XP за успешную проверку History\\n  - +5 XP за успешную проверку Perception\\n  - +5 XP за успешную проверку Philosophy\\n  - +10 репутация в выбранной группе за успешную проверку Persuasion\\n  - Разблокировка достижения \\"Наблюдатель Филадельфии\\" (при успешной проверке Perception)\\n  - Разблокировка достижения \\"Философ\\" (при успешной проверке Philosophy)\\n\\nНаграды за ветвления:\\n  - При изучении и идеалов, и фактов: +10 XP и полная информация о происхождении города\\n  - При сбалансированной оценке: +5 репутация среди всех групп и достижение \\"Философ Филадельфии\\"\\n  - При сбалансированной позиции: +10 репутация во всех группах и разблокировка квеста \\"Путь Филадельфии\\"\\n  - При оптимистичной трактовке: +15 репутация в Philadelphia Optimists и разблокировка квеста \\"Будущее Филадельфии\\"\\n  - При реалистичной трактовке: +15 репутация в Philadelphia Realists и разблокировка квеста \\"Реформы Филадельфии\\"\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест включает историко-философские размышления, моральный выбор и сбор городских легенд, влияющих на отношения\\nс гражданскими фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "npc_dialogues", "title": "Диалоги с NPC", "body": "Историк (Доктор Роберт Митчелл)\\nДиалог 1 (начальный):\\n  - Доктор Митчелл: \\"Добро пожаловать в историческую библиотеку! Вы интересуетесь происхождением Филадельфии?\\"\\n  - Игрок: \\"Да, расскажите о названии города.\\"\\n  - Доктор Митчелл: \\"Philadelphia происходит от греческих слов ''philos'' - любовь и ''adelphos'' - брат. Уильям Пенн основал город в 1682 году как убежище толерантности для квакеров.\\"\\n  - Игрок: \\"Что означает ''братская любовь''?\\"\\n  - Доктор Митчелл: \\"Это идеал взаимного уважения и религиозной свободы. Пенн хотел создать место, где все могли бы жить в мире, независимо от веры.\\"\\n\\nМестный житель (Сара Джонсон)\\nДиалог 2:\\n  - Сара: \\"Вы здесь первый раз? Филадельфия — это особое место. Город братской любви, как нас называли.\\"\\n  - Игрок: \\"Ощущается ли эта ''братская любовь'' сегодня?\\"\\n  - Сара: \\"Честно? Мы стараемся. Но современность вносит свои коррективы. Идеалы остаются, но реальность сложнее.\\"\\n  - Игрок: \\"Что вы думаете о наследии Пенна?\\"\\n  - Сара: \\"Наследие живёт в архитектуре, в праздниках, в нашем отношении к другим. Но времена меняются, и город меняется вместе с ними.\\"\\n\\nФилософ (Профессор Джеймс Уилсон)\\nДиалог 3:\\n  - Профессор Уилсон: \\"Интересная тема — идеалы против реальности. Вы пришли к выводу?\\"\\n  - Игрок: \\"Расскажите о контрасте между идеалами и реальностью.\\"\\n  - Профессор Уилсон: \\"Пенн мечтал о городе толерантности. Сегодня мы видим и успехи, и проблемы. Вопрос в том, как мы относимся к этому разрыву.\\"\\n  - Игрок: \\"Что важнее — идеалы или реальность?\\"\\n  - Профессор Уилсон: \\"Оба важны. Идеалы направляют нас, реальность показывает, куда двигаться. Главное — не терять веру в лучшее.\\"\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Ветвления и выборы", "body": "Выбор 1: Подход к изучению истории\\nУсловие: После изучения корней названия\\nВарианты:\\n  - Фокусироваться на идеалах Пенна: +5 XP, разблокируется дополнительная информация о квакерах\\n  - Фокусироваться на исторических фактах: +5 XP, разблокируется дополнительная информация о колонизации\\n  - Изучать и идеалы, и факты: +10 XP, разблокируется полная информация о происхождении города\\n\\nВыбор 2: Интерпретация современного состояния\\nУсловие: После сопоставления идеалов и реальности\\nВарианты:\\n  - Подчеркнуть успехи города: +10 репутация среди оптимистов, разблокируется достижение \\"Оптимист Филадельфии\\"\\n  - Подчеркнуть проблемы города: +10 репутация среди реалистов, разблокируется достижение \\"Реалист Филадельфии\\"\\n  - Сбалансированная оценка: +5 репутация среди всех групп, разблокируется достижение \\"Философ Филадельфии\\"\\n\\nВыбор 3: Финальная позиция\\nУсловие: При принятии морального выбора\\nВарианты:\\n  - Поддержать оптимистичную трактовку: +15 репутация в Philadelphia Optimists, разблокируется квест \\"Будущее Филадельфии\\"\\n  - Подчеркнуть разрыв между идеалом и реальностью: +15 репутация в Philadelphia Realists, разблокируется квест \\"Реформы Филадельфии\\"\\n  - Принять сбалансированную позицию: +10 репутация во всех группах, разблокируется квест \\"Путь Филадельфии\\"\\n", "mechanics_links": [], "assets": []}, {"id": "skill_checks", "title": "Проверки навыков", "body": "Проверка 1: Investigation (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок глубоко изучает исторические материалы, разблокируется полная информация о плане Пенна\\nПровал: Игрок получает только базовую информацию о названии города\\n\\nПроверка 2: History (Этап 2)\\nТребование: 0.3+\\nУспех: Игрок понимает исторический контекст прибытия колонистов, разблокируется дополнительная информация о первых поселенцах\\nПровал: Игрок получает только общую информацию о Penn''s Landing\\n\\nПроверка 3: Perception (Этап 3)\\nТребование: 0.4+\\nУспех: Игрок замечает детали города и реакции горожан, +5 XP и разблокируется достижение \\"Наблюдатель Филадельфии\\"\\nПровал: Игрок наблюдает только основные моменты, получает базовый опыт\\n\\nПроверка 4: Philosophy (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок глубоко анализирует контраст между идеалами и реальностью, разблокируется достижение \\"Философ\\"\\nПровал: Игрок получает только поверхностное понимание проблемы\\n\\nПроверка 5: Persuasion (Этап 5)\\nТребование: 0.3+\\nУспех: Игрок успешно аргументирует свою позицию, +10 репутация в выбранной группе\\nПровал: Игрок получает базовую репутацию, выбор всё равно учитывается\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи и продолжения", "body": "Связан с философской линией Филадельфии, заданиями об истории американской демократии и сюжетами о толерантности.\\n\\nСвязанные квесты:\\n  - \\"Филадельфия: история американской демократии\\" (разблокируется после завершения)\\n  - \\"Филадельфия: толерантность и вызовы\\" (разблокируется при выборе сбалансированной позиции)\\n  - \\"Филадельфия: будущее города\\" (разблокируется при оптимистичной трактовке)\\n  - \\"Филадельфия: реформы города\\" (разблокируется при реалистичной трактовке)\\n\\nВлияние на репутацию:\\n  - Philadelphia Community: +25 (базовая)\\n  - Philadelphia Historians: +15\\n  - Philadelphia Philosophers: +10\\n  - Philadelphia Optimists: +10/+15 (в зависимости от выбора)\\n  - Philadelphia Realists: +10/+15 (в зависимости от выбора)\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1092, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Город братской любви» в формат знаний."}, {"version": "1.1.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлен детальный контент: этапы с локациями, диалоги с NPC, проверки навыков (Investigation, History, Perception, Philosophy, Persuasion), ветвления и выборы, детальные награды."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

