-- Quest: canon-quest-tokyo-arasaka-infiltration
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-arasaka-infiltration', 'Токио 2061-2077 — «Проникновение в Arasaka»', 'Игроки планируют и реализуют высший класс миссии, выбирая между социальным обманом, хакерством или силовым штурмом Arasaka Tower.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-arasaka-infiltration", "title": "Токио 2061-2077 — «Проникновение в Arasaka»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T23:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["tokyo", "arasaka", "infiltration"], "topics": ["corporate-espionage", "relic", "stealth"], "related_systems": ["narrative-service", "world-service", "notoriety-system"], "related_documents": [{"id": "canon-quest-tokyo-relic-arc", "relation": "continuation"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2061-2077/quest-032-arasaka-infiltration.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "extreme"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест проникновения в токийскую башню Arasaka не был оформлен в формате, пригодном для интеграции в канон и системы репутаций.", "goal": "Систематизировать сценарий кражи данных «Relic 2.0» с учётом альтернативных путей и корпоративных последствий.", "essence": "Игроки планируют и реализуют высший класс миссии, выбирая между социальным обманом, хакерством или силовым штурмом Arasaka Tower.", "key_points": ["Многошаговое планирование с выбором подхода и высокими требованиями к персонажам.", "Легендарные награды, сопровождающиеся перманентной враждой с Arasaka.", "Использование эстетики японской корпоративной культуры и элитной охраны в стиле самураев."]}, "content": {"sections": [{"id": "briefing", "title": "Завязка и контекст", "body": "После слухов о проекте «Relic 2.0» игроки получают заказ на кражу протоколов из штаб-квартиры Arasaka в Токио.\\nНеобходимо изучить охрану, собрать инсайдерскую информацию и подготовить комманду, способную скрытно или демонстративно\\nпреодолеть защиту корпорации.\\n", "mechanics_links": [], "assets": []}, {"id": "approaches", "title": "Пути проникновения", "body": "- **Социальная инженерия:** внедриться под видом сотрудников, пройти корпоративные протоколы и получить доступ к secure-зонам.\\n- **Хакерство:** взломать сетевые сегменты удалённо или через закладку, контролируя системы безопасности и подменяя журналы.\\n- **Силовой штурм:** атаковать башню напрямую, рассчитывая на максимальную огневую мощь и поддержку техник.\\nКаждый путь имеет собственные DC (Обман 22, Хакерство 24, комплексный бой) и уникальные награды.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы операции", "body": "1. **Разведка:** сбор планов башни, расписаний смен и кодов допуска.\\n2. **Выбор стратегии:** распределение ролей, подготовка оборудования и определение точек входа.\\n3. **Проникновение:** выполнение выбранного подхода, минуя охрану и системы «Самураев Arasaka».\\n4. **Кража данных:** скачивание протоколов Relic 2.0 из защищённого сектора.\\n5. **Эвакуация:** скрыться из башни до прибытия оперативных сил Arasaka.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- **Опыт:** 8 000 XP.\\n- **Финансы:** 100 000 еддисов плюс доступ к продажам данных.\\n- **Лут:** чертежи Relic 2.0, легендарные импланты Arasaka (уникальные варианты для нетраннеров, штурмовиков и аналитиков).\\n- **Репутация:** мгновенный рост славы среди подполья и перманентный розыск со стороны Arasaka.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Ключевые механики", "body": "- **Стелс высокого уровня:** необходимость использовать звук, свет и социальные сигналы.\\n- **Множественные варианты прохождения:** каждое решение открывает дополнительные сценарии и побочные миссии.\\n- **Розыск:** после успеха Arasaka инициирует охоту, добавляя долгосрочные риски и специальные преследующие отряды.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Relic 2.0", "definition": "Экспериментальная версия нейроимпланта для сохранения сознаний в рамках лора Cyberpunk."}, {"term": "Самураи Arasaka", "definition": "Элитная охрана корпорации, сочетающая традиционные доспехи и высокотехнологичное вооружение."}], "references": [{"title": "Arasaka Corporation (Cyberpunk Wiki)", "link": "https://cyberpunk.fandom.com/wiki/Arasaka"}], "decisions": []}, "implementation": {"github_issue": 264, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "lore_team", "changes": "Конверсия квеста «Проникновение в Arasaka» в структурированный YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

